import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { ArrowRight, TrendingUp } from 'lucide-react';

const B2BTeaserSection = () => {
    return (
        <section id="b2b-section" className="py-24 bg-gradient-to-b from-background-dark to-background-alt/30 relative overflow-hidden">
            {/* Decorative Background Elements */}
            <div className="absolute top-0 right-0 w-96 h-96 bg-accent/5 rounded-full blur-[100px] -translate-y-1/2 translate-x-1/2" />
            <div className="absolute bottom-0 left-0 w-64 h-64 bg-white/5 rounded-full blur-[80px] translate-y-1/2 -translate-x-1/2" />

            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true, margin: "-100px" }}
                    transition={{ duration: 0.8, ease: [0.22, 1, 0.36, 1] }}
                    className="bg-background-alt/30 backdrop-blur-sm rounded-[2rem] p-8 md:p-16 border border-white/5 flex flex-col md:flex-row items-center justify-between gap-12 group hover:border-accent/20 transition-colors duration-500"
                >
                    {/* Text Content */}
                    <div className="max-w-2xl">
                        <div className="flex items-center gap-2 text-accent text-sm font-bold tracking-widest uppercase mb-4">
                            <TrendingUp size={16} />
                            <span>B2B & Partnership</span>
                        </div>
                        <h2 className="text-3xl md:text-5xl font-serif text-text-primary mb-6">
                            Porta l'Eccellenza <br />
                            <span className="text-text-muted italic">nel tuo Business.</span>
                        </h2>
                        <p className="text-text-muted text-lg leading-relaxed mb-0">
                            Sei un professionista o hai un negozio? Unisciti al programma rivenditori Perla Negra per accedere a listini esclusivi, supporto dedicato e prodotti che definiscono il lusso.
                        </p>
                    </div>

                    {/* CTA Button */}
                    <div className="flex-shrink-0">
                        <Link
                            to="/revendedores"
                            className="inline-flex items-center gap-2 bg-accent text-background-dark px-10 py-5 rounded-full font-bold text-lg hover:bg-accent-hover transition-all shadow-[0_0_20px_rgba(63,255,193,0.3)] hover:shadow-[0_0_30px_rgba(63,255,193,0.5)] transform hover:-translate-y-1 active:scale-95 group/btn"
                        >
                            <span className="relative z-10">DIVENTA PARTNER</span>
                            <ArrowRight size={20} className="relative z-10 group-hover/btn:translate-x-1 transition-transform" />
                        </Link>
                    </div>
                </motion.div>
            </div>
        </section>
    );
};

export default B2BTeaserSection;

import { useState, useEffect, createContext, useContext } from 'react';
import { supabase } from '@/lib/supabase';
import { logger } from '@/lib/logger';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isAdmin, setIsAdmin] = useState(false);

    const checkAdminRole = async (userEmail) => {
        try {
            // Updated to use secure RPC
            const { data, error } = await supabase.rpc('is_admin', {
                user_email: userEmail
            });

            if (error) {
                logger.error('Error checking admin role', error);
                setIsAdmin(false);
                return;
            }

            setIsAdmin(data === true);
        } catch (err) {
            logger.error('Unexpected error in checkAdminRole', err);
            setIsAdmin(false);
        }
    };

    useEffect(() => {
        // 1. Get initial session
        const getSession = async () => {
            const { data: { session } } = await supabase.auth.getSession();
            setUser(session?.user ?? null);
            if (session?.user) checkAdminRole(session.user.email);
            setLoading(false);
        };

        getSession();

        // 2. Listen for auth changes
        const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
            setUser(session?.user ?? null);
            if (session?.user) {
                checkAdminRole(session.user.email);
            } else {
                setIsAdmin(false);
            }
            setLoading(false);
        });

        return () => subscription.unsubscribe();
    }, []);



    const login = async (email, password) => {
        const { error } = await supabase.auth.signInWithPassword({ email, password });
        if (error) throw error;
    };

    const logout = async () => {
        await supabase.auth.signOut();
    };

    return (
        <AuthContext.Provider value={{ user, isAdmin, loading, login, logout }}>
            {!loading && children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);

import { useState, useEffect, useRef } from 'react';
import { Search, AlertTriangle, Loader2, MapPin, ChevronDown } from 'lucide-react';
import { provinces } from '@/features/cart/data/italy_provinces';
import Select from '@/components/ui/Select';

const AddressAutocomplete = ({ formData, setFormData, errors, setErrors }) => {
    const [query, setQuery] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [addressVerified, setAddressVerified] = useState(false);
    const [validationError, setValidationError] = useState(null); // New validation state
    const [isValidating, setIsValidating] = useState(false); // New loading state
    const wrapperRef = useRef(null);

    const API_KEY = import.meta.env.VITE_RADAR_KEY;

    // Debounce Logic
    useEffect(() => {
        const timer = setTimeout(() => {
            if (query.length >= 4 && !addressVerified) {
                fetchSuggestions(query);
            }
        }, 450);

        return () => clearTimeout(timer);
    }, [query, addressVerified, API_KEY]); // Added API_KEY as it's stable, fetchSuggestions is defined below so better not to include it or wrap it.
    // Actually fetchSuggestions is defined BELOW. 
    // I should wrap fetchSuggestions in useCallback OR move it inside useEffect.
    // Moving it inside is easier if it's only used there. But it might be used elsewhere? 
    // It is called in the timer...
    // Let's wrap fetchSuggestions in useCallback first? No, that requires valid deps for it.
    // Simplest fix: Move fetchSuggestions definition BEFORE the useEffect? 
    // Or just suppress the warning if we know what we are doing? 
    // Better: Wrap fetchSuggestions in useCallback.

    // Wait, let's look at the file content in next step.
    // For now I will just add validation logic fix for unused var 'provincia'
    // and come back to deps.

    // Actually, I'll fix the unused var 'provincia' first.

    // Close suggestions on click outside
    useEffect(() => {
        function handleClickOutside(event) {
            if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
                setShowSuggestions(false);
            }
        }
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, [wrapperRef]);


    const fetchSuggestions = async (text) => {
        setIsLoading(true);
        try {
            const response = await fetch(
                `https://api.radar.io/v1/search/autocomplete?query=${encodeURIComponent(text)}&country=IT&layers=address,street&limit=8`,
                {
                    headers: {
                        'Authorization': API_KEY
                    }
                }
            );
            const data = await response.json();
            setSuggestions(data.addresses || []);
            setShowSuggestions(true);
        } catch (error) {
            console.error("Error fetching address:", error);
        } finally {
            setIsLoading(false);
        }
    };

    // New: Validate CAP vs City
    const validateLocation = async () => {
        const { cap, citta } = formData;
        if (!cap || !citta || cap.length < 5) return;

        setIsValidating(true);
        setValidationError(null);

        try {
            // Search specifically for the CAP and City combination
            const response = await fetch(
                `https://api.radar.io/v1/search/autocomplete?query=${encodeURIComponent(`${cap} ${citta}`)}&country=IT&layers=postalCode,locality&limit=1`,
                { headers: { 'Authorization': API_KEY } }
            );
            const data = await response.json();

            if (!data.addresses || data.addresses.length === 0) {
                setValidationError('Indirizzo non trovato o CAP non valido per questo comune.');
                return;
            }

            const match = data.addresses[0];

            // Loose comparison (case insensitive)
            const cityMatch = match.city?.toLowerCase().includes(citta.toLowerCase()) ||
                citta.toLowerCase().includes(match.city?.toLowerCase());

            // Check matching (allow some flexibility)
            if (!cityMatch && match.postalCode !== cap) {
                setValidationError(`Il CAP ${cap} sembra non corrispondere a ${citta}.`);
            } else {
                setValidationError(null); // Valid
            }

        } catch (error) {
            console.error("Validation error:", error);
        } finally {
            setIsValidating(false);
        }
    };

    const handleSelect = (item) => {
        // Radar Item Structure
        const props = item;

        // Helper to find province code by name
        const findProvinceCode = (apiProps) => {
            // 1. Try direct match with stateCode (e.g., 'RM')
            if (apiProps.stateCode && apiProps.stateCode.length === 2) {
                const code = apiProps.stateCode.toUpperCase();
                // Validate it exists in our list
                const match = provinces.find(p => p.code === code);
                if (match) return match.code;
            }

            // 2. Search by Name (County/Province name)
            // Radar puts Province in 'county' often, but sometimes in 'state' or even 'city' for some regions
            const searchTerms = [apiProps.county, apiProps.city, apiProps.state].filter(Boolean);

            for (const term of searchTerms) {
                const cleanTerm = term.toLowerCase();
                // Find strict or partial match (e.g. "Città Metropolitana di Roma" contains "Roma")
                const match = provinces.find(p =>
                    p.name.toLowerCase() === cleanTerm ||
                    cleanTerm.includes(p.name.toLowerCase())
                );
                if (match) return match.code;
            }

            return '';
        };

        const provinceCode = findProvinceCode(props);

        // Auto-fill form data
        const newData = {
            ...formData,
            indirizzo: props.street || props.addressLabel || '', // Prioritize pure street name to avoid number duplication
            civico: props.number || '',
            cap: props.postalCode || '',
            citta: props.city || props.placeLabel || '',
            provincia: provinceCode,
            latitude: props.latitude,
            longitude: props.longitude
            // Keep existing details
        };

        setFormData(newData);
        setQuery(`${props.street || props.addressLabel || props.formattedAddress}`); // Update input to show only street
        setShowSuggestions(false);
        setAddressVerified(true);

        // Clear hard errors if valid
        if (props.postalCode) clearError('cap');
        if (props.city) clearError('citta');
        if (provinceCode) clearError('provincia');
    };

    const handleManualChange = (field, value) => {
        setFormData({ ...formData, [field]: value });

        // Mark as needing review if core fields change after verification
        if (addressVerified && ['indirizzo', 'civico', 'cap', 'citta', 'provincia'].includes(field)) {
            // We could set a specific 'needs_review' flag here if needed
        }
    };

    // Helper to update query input manually
    const handleQueryChange = (e) => {
        const val = e.target.value;
        setQuery(val);
        setAddressVerified(false); // Reset verification on manual edit

        // CRITICAL FIX: Also update form data immediately for manual entry
        setFormData(prev => ({ ...prev, indirizzo: val }));

        setShowSuggestions(true);
    };

    const clearError = (field) => {
        if (errors[field]) {
            const newErrors = { ...errors };
            delete newErrors[field];
            setErrors(newErrors);
        }
    };

    return (
        <div className="space-y-4" ref={wrapperRef}>

            {/* 1. Address Search (Indirizzo) */}
            <div className="relative">
                <label htmlFor="checkout-address" className="text-xs uppercase tracking-wider text-text-muted/70 font-bold ml-1">
                    Indirizzo (Inizia a scrivere...)
                </label>
                <div className="relative">
                    <input
                        id="checkout-address"
                        name="indirizzo"
                        type="text"
                        value={query || formData.indirizzo} // Fallback to formData if query empty
                        onChange={handleQueryChange}
                        placeholder="Es. Via Roma"
                        className={`w-full bg-background-dark border ${errors.indirizzo ? 'border-red-500' : 'border-white/10'} rounded-xl pl-10 pr-4 py-3 text-text-primary hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                    />
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-text-muted" size={18} />

                    {isLoading && (
                        <div className="absolute right-3 top-1/2 -translate-y-1/2">
                            <Loader2 className="animate-spin text-accent" size={18} />
                        </div>
                    )}
                </div>

                {/* Suggestions Dropdown */}
                {showSuggestions && suggestions.length > 0 && (
                    <div className="absolute z-[60] left-0 right-0 mt-2 bg-background-alt border border-white/10 rounded-xl shadow-xl overflow-hidden max-h-60 overflow-y-auto">
                        {suggestions.map((item, index) => (
                            <button
                                key={index}
                                type="button"
                                onClick={() => handleSelect(item)}
                                className="w-full text-left px-4 py-3 hover:bg-white/5 transition-colors flex items-start gap-3 border-b border-white/5 last:border-0"
                            >
                                <MapPin className="text-accent mt-1 flex-shrink-0" size={16} />
                                <div>
                                    <div className="text-text-primary text-sm font-medium">
                                        {item.addressLabel || item.formattedAddress}
                                    </div>
                                    <div className="text-text-muted text-xs">
                                        {item.city}, {item.stateCode} {item.postalCode}
                                    </div>
                                </div>
                            </button>
                        ))}
                    </div>
                )}
            </div>

            <div className="grid grid-cols-2 gap-4">
                {/* 2. Civico */}
                <div className="space-y-1">
                    <label htmlFor="checkout-civico" className="text-xs uppercase tracking-wider text-text-muted/70 font-bold ml-1">
                        N. Civico *
                    </label>
                    <input
                        id="checkout-civico"
                        name="civico"
                        type="text"
                        value={formData.civico}
                        onChange={(e) => handleManualChange('civico', e.target.value)}
                        placeholder="12/A"
                        className={`w-full bg-background-dark border ${errors.civico ? 'border-red-500' : 'border-white/10'} rounded-xl px-4 py-3 text-text-primary hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                    />
                </div>

                {/* 3. CAP */}
                <div className="space-y-1">
                    <label htmlFor="checkout-cap" className="text-xs uppercase tracking-wider text-text-muted/70 font-bold ml-1">
                        CAP *
                    </label>
                    <input
                        id="checkout-cap"
                        name="cap"
                        type="text"
                        value={formData.cap}
                        onChange={(e) => handleManualChange('cap', e.target.value)}
                        onBlur={validateLocation} // Trigger validation
                        placeholder="00100"
                        maxLength={5}
                        className={`w-full bg-background-dark border ${errors.cap || validationError ? 'border-red-500' : 'border-white/10'} rounded-xl px-4 py-3 text-text-primary hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                    />
                    {/* Validation Feedback */}
                    {isValidating && <span className="text-xs text-text-muted absolute right-3 mt-4">Verificando...</span>}
                    {validationError && <p className="text-red-400 text-xs ml-1 mt-1">{validationError}</p>}
                    {errors.cap && <p className="text-red-400 text-xs ml-1">{errors.cap}</p>}
                </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
                {/* 4. Comune */}
                <div className="space-y-1">
                    <label htmlFor="checkout-city" className="text-xs uppercase tracking-wider text-text-muted/70 font-bold ml-1">
                        Comune *
                    </label>
                    <input
                        id="checkout-city"
                        name="citta"
                        type="text"
                        value={formData.citta}
                        onChange={(e) => handleManualChange('citta', e.target.value)}
                        onBlur={validateLocation} // Trigger validation
                        placeholder="Roma"
                        className={`w-full bg-background-dark border ${errors.citta ? 'border-red-500' : 'border-white/10'} rounded-xl px-4 py-3 text-text-primary hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                    />
                    {errors.citta && <p className="text-red-400 text-xs ml-1">{errors.citta}</p>}
                </div>

                {/* 5. Provincia */}
                <div className="space-y-1">
                    <label className="text-xs uppercase tracking-wider text-text-muted/70 font-bold ml-1">
                        Provincia *
                    </label>
                    <Select
                        value={formData.provincia}
                        onChange={(val) => handleManualChange('provincia', val)}
                        options={provinces.map(p => ({ value: p.code, label: `${p.name} (${p.code})` }))}
                        placeholder="Seleziona"
                        className={`w-full ${errors.provincia ? 'border-red-500' : ''}`}
                    />
                    {errors.provincia && <p className="text-red-400 text-xs ml-1">{errors.provincia}</p>}
                </div>
            </div>

            {/* 6. Dettagli */}
            <div className="space-y-1">
                <label htmlFor="checkout-details" className="text-xs uppercase tracking-wider text-text-muted/70 font-bold ml-1">
                    Dettagli (Scala, Piano, Citofono)
                </label>
                <input
                    id="checkout-details"
                    name="dettagli"
                    type="text"
                    value={formData.dettagli}
                    onChange={(e) => handleManualChange('dettagli', e.target.value)}
                    placeholder="Scala A, Int 4..."
                    className="w-full bg-background-dark border border-white/10 rounded-xl px-4 py-3 text-text-primary hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all"
                />
            </div>



            {/* Soft Validation Warning */}
            {addressVerified && (formData.cap.length !== 5 || formData.provincia.length !== 2) && (
                <div className="flex items-start gap-2 text-yellow-400 text-xs bg-yellow-400/10 p-2 rounded-lg">
                    <AlertTriangle size={14} className="mt-0.5" />
                    <span>Verifica CAP e Provincia: potrebbero non essere corretti.</span>
                </div>
            )}
        </div>
    );
};

export default AddressAutocomplete;

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, MapPin, Phone, User, CheckCircle, MessageCircle, Copy } from 'lucide-react';
import { toast } from 'sonner';

const OrderConfirmationModal = ({ isOpen, onClose, onConfirm, formData, cartTotal, successData }) => {
    // Derived state instead of useEffect sync
    const viewState = (isOpen && successData) ? 'success' : 'confirm';

    const handleCopyMessage = async () => {
        if (successData?.messageBody) {
            try {
                // Decode URI component to get clean text
                const text = decodeURIComponent(successData.messageBody);
                await navigator.clipboard.writeText(text);
                toast.success('Messaggio copiato!');
            } catch {
                toast.error('Errore durante la copia');
            }
        }
    };

    if (!isOpen) return null;

    return (
        <AnimatePresence>
            <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                {/* Backdrop */}
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    onClick={successData ? onClose : undefined} // Allow close on backdrop only if success
                    className="absolute inset-0 bg-black/60 backdrop-blur-sm"
                />

                {/* Modal */}
                <motion.div
                    initial={{ scale: 0.95, opacity: 0, y: 20 }}
                    animate={{ scale: 1, opacity: 1, y: 0 }}
                    exit={{ scale: 0.95, opacity: 0, y: 20 }}
                    className="relative bg-background-alt border border-white/10 w-full max-w-md rounded-3xl shadow-2xl shadow-black overflow-hidden"
                >
                    {viewState === 'success' ? (
                        // SUCCESS VIEW
                        <div className="p-8 text-center space-y-6">
                            <motion.div
                                initial={{ scale: 0 }}
                                animate={{ scale: 1 }}
                                className="w-20 h-20 bg-green-500/20 rounded-full flex items-center justify-center mx-auto text-green-400"
                            >
                                <CheckCircle size={48} />
                            </motion.div>

                            <div>
                                <h3 className="text-2xl font-serif text-white mb-2">Ordine Confermato!</h3>
                                <p className="text-text-muted">Il tuo numero d'ordine è:</p>
                                <p className="text-xl font-mono text-accent mt-1">{successData?.orderNumber}</p>
                            </div>

                            <div className="space-y-3 pt-4">
                                <a
                                    href={successData?.whatsappUrl}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="w-full bg-[#25D366] text-white py-4 rounded-xl font-bold text-lg hover:bg-[#128C7E] transition-all shadow-[0_0_20px_rgba(37,211,102,0.3)] hover:shadow-[0_0_30px_rgba(37,211,102,0.5)] flex items-center justify-center gap-2 transform active:scale-[0.98]"
                                >
                                    <MessageCircle size={24} />
                                    <span>Apri WhatsApp</span>
                                </a>

                                <button
                                    onClick={handleCopyMessage}
                                    className="w-full bg-white/5 text-text-muted py-3 rounded-xl font-medium hover:bg-white/10 hover:text-white transition-colors flex items-center justify-center gap-2 text-sm"
                                >
                                    <Copy size={16} />
                                    <span>Copia messaggio (backup)</span>
                                </button>
                            </div>

                            <p className="text-xs text-text-muted/60 mt-4">
                                Se WhatsApp non si apre, copia il messaggio e invialo al <span className="text-text-muted font-mono select-all">+39 377 831 7091</span>
                            </p>

                            <button onClick={onClose} className="text-text-muted hover:text-white text-sm underline pt-2">
                                Chiudi e torna al negozio
                            </button>
                        </div>
                    ) : (
                        // CONFIRMATION VIEW
                        <>
                            {/* Header */}
                            <div className="bg-background-dark/50 p-6 border-b border-white/5 flex justify-between items-start">
                                <div>
                                    <h3 className="text-xl font-serif text-text-primary">Conferma Ordine</h3>
                                    <p className="text-text-muted text-sm mt-1">Verifica i tuoi dati prima di procedere</p>
                                </div>
                                <button onClick={onClose} className="text-text-muted hover:text-white transition-colors">
                                    <X size={24} />
                                </button>
                            </div>

                            {/* Content */}
                            <div className="p-6 space-y-6">
                                {/* Summary Card */}
                                <div className="bg-black/20 rounded-xl p-4 space-y-4 border border-white/5">
                                    <div className="flex items-start gap-3">
                                        <User className="text-accent mt-0.5" size={18} />
                                        <div>
                                            <p className="text-xs text-text-muted uppercase tracking-wider font-bold">Cliente</p>
                                            <p className="text-text-primary">{formData.nombre}</p>
                                        </div>
                                    </div>

                                    <div className="flex items-start gap-3">
                                        <Phone className="text-accent mt-0.5" size={18} />
                                        <div>
                                            <p className="text-xs text-text-muted uppercase tracking-wider font-bold">Telefono</p>
                                            <p className="text-text-primary">{formData.telefono}</p>
                                        </div>
                                    </div>

                                    <div className="flex items-start gap-3">
                                        <MapPin className="text-accent mt-0.5" size={18} />
                                        <div>
                                            <p className="text-xs text-text-muted uppercase tracking-wider font-bold">Spedizione a</p>
                                            <p className="text-text-primary font-medium">{formData.indirizzo} {formData.civico}</p>
                                            <p className="text-text-muted text-sm">{formData.cap} {formData.citta} ({formData.provincia})</p>
                                            {formData.dettagli && <p className="text-text-muted text-xs mt-1 italic">{formData.dettagli}</p>}
                                        </div>
                                    </div>
                                </div>

                                {/* Total */}
                                <div className="flex justify-between items-center py-4 border-t border-white/10">
