import { useParams, Link } from 'react-router-dom';
import { useProducts } from '@/features/products/hooks/useProducts';
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronRight, ShoppingBag, Star, Check } from 'lucide-react';
import { useCart } from '@/features/cart/context/CartContext';
import { toast } from 'sonner';
import SEO from '@/components/ui/SEO';
import Reveal from '@/components/ui/Reveal';
import { Skeleton } from '@/components/ui/Skeleton';
import ProductCard from '@/features/products/components/ProductCard';
import { trackViewItem, trackAddToCart } from '@/lib/analytics';
import AccordionItem from '@/components/ui/AccordionItem';

// Helper function to properly capitalize product names
const toTitleCase = (str) => {
    if (!str) return '';
    return str.toLowerCase().replace(/\b\w/g, (char) => char.toUpperCase());
};

const ProductDetailPage = () => {
    const { slug } = useParams();

    const { addToCart } = useCart();

    const { products, loading } = useProducts();
    const product = products.find(p => p.slug === slug);

    // Zoom State
    const [isHovering, setIsHovering] = useState(false);
    const [zoomPosition, setZoomPosition] = useState({ x: 0, y: 0 });

    // Image Gallery State
    const [activeImage, setActiveImage] = useState(null);
    const [image2Error, setImage2Error] = useState(false); // New: Track if secondary image is broken

    // Initialize active image when product loads
    useEffect(() => {
        if (product) {
            setActiveImage(product.image);
            setImage2Error(false); // Reset error state ONLY when changing products
        }
    }, [slug, product]); // Fix: properly react to product changes

    // Product State (Restored)
    const [quantity, setQuantity] = useState(1);

    // Reset quantity when slug changes (new product loaded)
    useEffect(() => {
        setQuantity(1);
    }, [slug]);

    // Sticky Mobile Bar Logic
    const [showStickyBar, setShowStickyBar] = useState(false);
    const [addToCartInView, setAddToCartInView] = useState(true);

    // Force scroll to top instantly on mount to prevent "stuck at bottom"
    useLayoutEffect(() => {
        window.scrollTo(0, 0);
    }, [slug]);

    // Intersection Observer for the main "Add to Cart" button
    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                setAddToCartInView(entry.isIntersecting);
            },
            { threshold: 0.1, rootMargin: "-20px" }
        );

        const btn = document.getElementById('main-add-to-cart-btn');
        if (btn) observer.observe(btn);

        return () => {
            if (btn) observer.unobserve(btn);
        };
    }, [product, slug]);

    useEffect(() => {
        // Show sticky bar ONLY if main button is NOT in view AND we have scrolled down a bit
        setShowStickyBar(!addToCartInView && window.scrollY > 200);
    }, [addToCartInView]);

    // Track View Item - Must be before any conditional returns (Rules of Hooks)
    useEffect(() => {
        if (product) {
            trackViewItem(product);
        }
    }, [product]);

    if (loading) {
        return (
            <div className="min-h-screen bg-background-dark py-6 flex flex-col pt-24">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full">
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-16">
                        {/* Image Skeleton */}
                        <Skeleton className="h-[500px] w-full rounded-3xl" />
                        {/* Content Skeleton */}
                        <div className="space-y-6 pt-8">
                            <Skeleton className="h-10 w-3/4" />
                            <Skeleton className="h-6 w-1/2 opacity-70" />
                            <div className="flex justify-between py-6">
                                <Skeleton className="h-8 w-24" />
                                <Skeleton className="h-8 w-16 rounded-full" />
                            </div>
                            <div className="space-y-2">
                                <Skeleton className="h-4 w-full" />
                                <Skeleton className="h-4 w-full" />
                                <Skeleton className="h-4 w-2/3" />
                            </div>
                            <div className="flex gap-4 pt-4">
                                <Skeleton className="h-12 w-32 rounded-full" />
                                <Skeleton className="h-12 flex-grow rounded-full" />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    if (!product) {
        return <div className="text-white text-center py-20">Prodotto non trovato</div>;
    }

    const formatText = (text) => {
        if (!text) return "";
        return text.charAt(0).toUpperCase() + text.slice(1).toLowerCase();
    };

    const handleAddToCart = () => {
        addToCart(product, quantity);
        trackAddToCart(product, quantity); // Analytics
        toast.success(`Aggiunto al carrello: ${product.name} `, {
            description: `${quantity} x ${product.name} `
        });
    };

    const handleMouseMove = (e) => {
        const { left, top, width, height } = e.currentTarget.getBoundingClientRect();
        const x = ((e.clientX - left) / width) * 100;
        const y = ((e.clientY - top) / height) * 100;
        setZoomPosition({ x, y });
    };

    // Helper to generate invented content
    const getMockData = (category) => {
        switch (category) {
            case 'Lubricantes': // Keeping category keys as they match DB potentially, but content must be IT
                return {
                    usage: "Applicare una piccola quantità sulla zona intima e massaggiare delicatamente. Compatibile con preservativi in lattice. Riapplicare secondo necessità per mantenere l'idratazione e il comfort.",
                    ingredients: "Aqua, Glycerin, Propylene Glycol, Hydroxyethylcellulose, Sodium Benzoate, Polysorbate 20, Disodium EDTA, Lactic Acid.",
                    tips: "Per un'esperienza più intensa, applicane un po' sulle zone erogene prima del contatto. Conservare in un luogo fresco e asciutto, lontano dalla luce solare diretta.",
                    sensation: "Idratante • Setoso • Base Acqua"
                };
            case 'Fragancias':
                return {
                    usage: "Spruzzare sui punti di pulsazione: polsi, collo e dietro le orecchie. Lasciare che la fragranza si assesti senza strofinare. Ideale da usare dopo la doccia.",
                    ingredients: "Alcohol Denat, Aqua (Water), Parfum (Fragrance), Linalool, Limonene, Citronellol, Coumarin, Geraniol.",
                    tips: "I feromoni si attivano con il calore corporeo. Applicalo 15 minuti prima di uscire per farlo fondere con la tua chimica naturale.",
                    sensation: "Seducente • Intenso • Floreale/Legnoso"
                };
            case 'Vigorizzanti': // Formerly Afrodisiacos
                return {
                    usage: "Assumere con abbondante acqua o mescolare con la tua bevanda preferita. Non superare la dose giornaliera raccomandata. Effetto previsto in 30-45 minuti.",
                    ingredients: "Estratto di Maca, Ginseng, L-Arginina, Vitamine del complesso B, Zinco, Guaranà.",
                    tips: "Combinalo con un ambiente rilassato e stimolazione preliminare per massimizzare gli effetti.",
                    sensation: "Stimolante • Energetico • Vigorizzante"
                };
            case 'Olio commestibile':
                return {
                    usage: "Versare una piccola quantità sulle mani o direttamente sul corpo. Massaggiare dolcemente. Essendo commestibile, è sicuro per baci e giochi orali.",
                    ingredients: "Glicerina vegetale, Aroma naturale, Acqua, Saccarina sodica (dolcificante), Coloranti alimentari.",
                    tips: "Soffia delicatamente sulla zona applicata per attivare un piacevole effetto calore che intensifica la sensazione.",
                    sensation: "Gustoso • Riscaldante • Scivoloso"
                };
            case 'Gioco':
                return {
                    usage: "Seguire le regole incluse nella confezione. Stabilire un 'safe word' o limiti chiari con il partner prima di iniziare per garantire un'esperienza divertente e sicura.",
                    ingredients: "Carta di alta qualità, cartone rinforzato, dadi in resina, accessori in tessuto satinato.",
                    tips: "Create l'atmosfera giusta con musica e luci soffuse. Usate il gioco come pretesto per esplorare nuove fantasie e rompere la routine.",
                    sensation: "Divertente • Intrigante • Complice"
                };
            default:
                return {
                    usage: "Utilizzare secondo le istruzioni sulla confezione. Pulire prima e dopo ogni utilizzo con acqua tiepida e sapone neutro o un detergente specifico per giocattoli.",
                    ingredients: "Materiali sicuri per il corpo, privi di ftalati. Silicone di grado medico o ABS.",
                    tips: "Esplora diversi ritmi e pressioni. Inizia delicatamente e aumenta l'intensità in base al tuo comfort.",
                    sensation: "Piacevole • Versatile • Sicuro"
                };
        }
    };

    // Priority: DB fields -> Mock Fallback
    const mockDataFallback = getMockData(product.category);

    // Construct display data using DB fields if available, otherwise use mock
    const displayData = {
        usage: product.usage || mockDataFallback.usage,
        ingredients: product.ingredients || mockDataFallback.ingredients,
        tips: product.tips || mockDataFallback.tips,
        sensation: product.sensation || mockDataFallback.sensation
    };

    // Structured Data for SEO (Rich Snippets)
    const structuredData = {
        "@context": "https://schema.org/",
        "@type": "Product",
        "name": product.name,
        "image": product.image,
        "description": product.description,
        "sku": product.code || product.slug,
        "mpn": product.code || product.slug,
        "brand": {
            "@type": "Brand",
            "name": product.brand || "Perla Negra"
        },
        "offers": {
            "@type": "Offer",
            "url": window.location.href,
            "priceCurrency": "EUR",
            "price": product.price,
            "availability": "https://schema.org/InStock",
            "itemCondition": "https://schema.org/NewCondition"
        }
    };

    return (
        <div className="bg-background-dark min-h-screen py-6 flex flex-col pb-32"> {/* Added pb-32 to provide breathing room from sticky bar */}
            <script
                type="application/ld+json"
                dangerouslySetInnerHTML={{ __html: JSON.stringify(structuredData) }}
            />
            <SEO
                key={product.id}
                title={product.name}
                description={`Acquista ${product.name} - ${product.subtitle || product.category} `}
                image={product.image}
            />
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full flex-grow flex flex-col">

                {/* Breadcrumbs */}
                <nav className="flex items-center text-sm text-text-muted mb-4 space-x-2 flex-shrink-0 pt-24">
                    <Link to="/" className="hover:text-accent">Home</Link>
                    <ChevronRight size={14} />
                    <Link to="/productos" className="hover:text-accent">Prodotti</Link>
                    <ChevronRight size={14} />
                    <span className="text-accent">{product.category}</span>
                </nav>

                {/* Main Content Grid - Responsive layout without fixed height constraints */}
                <div className="grid grid-cols-1 lg:grid-cols-12 gap-6 lg:gap-12 items-start">

                    {/* Left: Main Image Card & Thumbnails - Sticky on Desktop */}
                    <div className="w-full lg:col-span-5 flex flex-col gap-6 lg:sticky lg:top-24">
                        <div
                            className="bg-background-alt/50 rounded-3xl overflow-hidden relative border border-border/10 group cursor-crosshair w-full aspect-[4/5] flex items-center justify-center"
                            onMouseMove={handleMouseMove}
                            onMouseEnter={() => setIsHovering(true)}
                            onMouseLeave={() => setIsHovering(false)}
                        >
                            {/* 1. Ambient Background Layer */}
                            <div className="absolute inset-0 z-0">
                                <img
                                    src={activeImage || product.image}
                                    alt=""
                                    className="w-full h-full object-cover blur-2xl opacity-40 scale-110"
                                />
                            </div>

                            {/* 2. Main Product Image */}
                            <div style={{
                                width: '100%',
                                height: '100%',
                                position: 'relative',
                                zIndex: 10,
                                padding: '16px'
                            }}>
                                <motion.div
                                    style={{
                                        width: '100%',
                                        height: '100%',
                                        transformOrigin: `${zoomPosition.x}% ${zoomPosition.y}%`,
                                        transform: isHovering ? 'scale(2)' : 'scale(1)',
                                        transition: 'transform 0.2s ease-out'
                                    }}
                                >
                                    <img
                                        src={activeImage || product.image}
                                        alt={product.name}
                                        className="w-full h-full object-contain drop-shadow-xl"
                                    />
                                </motion.div>
                            </div>
                        </div>

                        {/* Thumbnails Gallery - Show if secondary images exist */}
                        {(product.image2 || product.image3) && (
                            <div className="flex gap-4 justify-center">
                                {/* Thumb 1 (Main) */}
                                <button
                                    onClick={() => setActiveImage(product.image)}
                                    className={`relative w-20 h-20 rounded-xl overflow-hidden border-2 transition-all bg-white ${activeImage === product.image ? 'border-accent shadow-[0_0_10px_rgba(63,255,193,0.3)]' : 'border-border/20 hover:border-accent/50'}`}
                                >
                                    <img src={product.image} alt="Vista principal" className="w-full h-full object-contain p-0 bg-white mix-blend-multiply" />
                                </button>

                                {/* Thumb 2 */}
                                {product.image2 && !image2Error && (
                                    <button
                                        onClick={() => setActiveImage(product.image2)}
                                        className={`relative w-20 h-20 rounded-xl overflow-hidden border-2 transition-all bg-white ${activeImage === product.image2 ? 'border-accent shadow-[0_0_10px_rgba(63,255,193,0.3)]' : 'border-border/20 hover:border-accent/50'}`}
                                    >
                                        <img
                                            src={product.image2}
                                            alt="Vista secundaria"
                                            className="w-full h-full object-contain p-0 bg-white mix-blend-multiply"
                                            onError={(e) => {
                                                e.target.style.display = 'none';
                                                setImage2Error(true);
                                            }}
                                        />
                                    </button>
                                )}

                                {/* Thumb 3 */}
                                {product.image3 && (
                                    <button
                                        onClick={() => setActiveImage(product.image3)}
                                        className={`relative w-20 h-20 rounded-xl overflow-hidden border-2 transition-all bg-white ${activeImage === product.image3 ? 'border-accent shadow-[0_0_10px_rgba(63,255,193,0.3)]' : 'border-border/20 hover:border-accent/50'}`}
                                    >
                                        <img
                                            src={product.image3}
                                            alt="Vista extra"
                                            className="w-full h-full object-contain p-0 bg-white mix-blend-multiply"
                                            onError={(e) => e.target.style.display = 'none'}
                                        />
                                    </button>
                                )}
                            </div>
                        )}
                    </div>

                    {/* Right: Info Card - COMPACT VERSION */}
                    <div className="lg:col-span-7 flex flex-col h-full">
                        {/* Unified Card with REDUCED PADDING and COMPACT SPACINGS */}
                        <div className="bg-background-alt px-6 py-6 rounded-3xl border border-border/20 flex flex-col h-full gap-4">

                            {/* Header Group - Compact */}
                            <div>
                                <h1 className="text-3xl font-serif text-text-primary mb-1 leading-tight">{toTitleCase(product.name)}</h1>
                                <p className="text-base text-text-muted font-light italic">{product.subtitle}</p>
                            </div>

                            {/* Price & Rating - Compact */}
                            <div className="flex items-center justify-between mb-2">
                                <div className="text-3xl font-bold text-accent">
                                    €{product.price.toFixed(2)}
                                </div>
                                <div className="flex items-center gap-1 bg-background-dark/30 px-2 py-1 rounded-full">
                                    <Star size={12} className="text-yellow-500 fill-yellow-500" />
                                    <span className="text-xs font-bold text-text-primary">4.9</span>
                                </div>
                            </div>

                            {/* Content Group - Compact Font */}
                            <p className="text-text-muted text-sm leading-relaxed first-letter:uppercase">
                                {product.description || "Lorem ipsum dolor sit amet consectetur. Placerat arcu at non consequat phasellus mi morbi maecenas."}
                            </p>

                            {/* Interactive Section: Quantity + Add to Cart Row - Compact Height */}
                            <div className="flex flex-col lg:flex-row gap-3 pt-6" id="main-add-to-cart-btn">
                                {/* Quantity - Smaller Height */}
                                <div className="flex items-center border border-border/30 rounded-full bg-background-dark/50 px-2 min-w-[120px] h-[48px]">
                                    <button
                                        onClick={() => setQuantity(q => Math.max(1, q - 1))}
                                        className="w-10 h-full flex items-center justify-center text-text-muted hover:text-accent text-lg"
                                    >
                                        -
                                    </button>
                                    <div className="flex-grow text-center font-bold text-text-primary text-base">{quantity}</div>
                                    <button
                                        onClick={() => setQuantity(q => q + 1)}
                                        className="w-10 h-full flex items-center justify-center text-text-muted hover:text-accent text-lg"
                                    >
                                        +
                                    </button>
                                </div>

                                {/* Add Button - Smaller Height/Text */}
                                <motion.button
                                    onClick={handleAddToCart}
                                    whileHover={{ scale: 1.02 }}
                                    whileTap={{ scale: 0.95 }}
                                    className="w-full h-[48px] rounded-full font-bold text-base flex items-center justify-center gap-2 bg-accent text-background-dark shadow-[0_0_20px_rgba(63,255,193,0.3)] hover:shadow-[0_0_30px_rgba(63,255,193,0.5)] transition-all"
                                >
                                    <span>Aggiungi al Carrello</span>
                                    <ShoppingBag size={18} />
                                </motion.button>
                            </div>

                            {/* Enhanced Explore CTA - Compact */}
                            <div className="flex justify-center mt-1">
                                <Link
                                    to="/productos"
                                    className="group flex items-center gap-2 px-4 py-2 border border-accent/20 rounded-full hover:bg-accent/10 hover:border-accent transition-all duration-300 text-accent font-medium text-xs uppercase tracking-wide"
                                >
                                    <span>Continua a esplorare</span>
                                    <ChevronRight size={14} className="group-hover:translate-x-1 transition-transform" />
                                </Link>
                            </div>

                            {/* Info Box - Boxed Style - COMPACT ROWS */}
                            <div className="mt-4 border border-border/10 rounded-2xl overflow-hidden bg-background-dark/20 text-xs">
                                <div className="flex flex-col divide-y divide-border/10">
                                    <div className="flex justify-between items-center px-4 py-3 hover:bg-white/5 transition-colors">
                                        <span className="text-text-muted font-medium">Sensazione</span>
                                        <span className="text-text-primary text-right font-medium">{formatText(displayData.sensation) || "Standard"}</span>
                                    </div>
                                    <div className="flex justify-between items-center px-4 py-3 hover:bg-white/5 transition-colors">
                                        <span className="text-text-muted font-medium">Formato</span>
                                        <span className="text-text-primary text-right font-medium">
                                            {(product.size || product.sizeFlOz) ? (
                                                <>
                                                    {product.size && <span>{product.size}</span>}
                                                    {product.size && product.sizeFlOz && <span className="text-text-muted/40 mx-2">|</span>}
                                                    {product.sizeFlOz && <span>{product.sizeFlOz}</span>}
                                                </>
                                            ) : (
                                                <span className="text-text-muted italic">N/A</span>
                                            )}
                                        </span>
                                    </div>
                                    <div className="flex justify-between items-center px-4 py-3 hover:bg-white/5 transition-colors">
                                        <span className="text-text-muted font-medium">Marca</span>
                                        <span className="text-text-primary text-right font-medium">{formatText(product.brand)}</span>
                                    </div>
                                    {product.code && (
                                        <div className="flex justify-between items-center px-4 py-3 hover:bg-white/5 transition-colors">
                                            <span className="text-text-muted font-medium">Codice</span>
                                            <span className="text-text-primary text-right font-bold uppercase tracking-wider">{product.code}</span>
                                        </div>
                                    )}
                                </div>
                            </div>

                            {/* Footer - Compact */}
                            <div className="flex flex-col gap-2 pt-4 flex-grow justify-center items-center text-center">
                                <div className="flex items-center gap-2 text-accent/80 text-[10px] font-medium bg-accent/5 p-2 rounded-lg border border-accent/10">
                                    <Check size={12} /> Pacco anonimo - Nessuna etichetta esterna
                                </div>
                                <div className="flex items-center gap-2 text-text-muted/60 text-[10px] px-2">
                                    <Check size={12} /> Spedizione veloce e discreta
                                </div>
                            </div>

                        </div>
                    </div>
                </div>

                {/* Bottom Section: Accordions */}
                <div className="max-w-4xl mx-auto w-full mt-24 pb-12">
                    <Reveal width="100%">
                        <div className="space-y-4">
                            <AccordionItem title="Descrizione Completa">
                                <p className="text-text-muted leading-relaxed first-letter:uppercase whitespace-pre-line text-sm">
                                    {(product.descriptionAdditional ? product.descriptionAdditional : product.description)
                                        .replace(/\s{4,}/g, '\n')
                                        .split('\n')
                                        .map(line => formatText(line))
                                        .join('\n')}
                                </p>
                            </AccordionItem>

                            <AccordionItem title="Ingredienti">
                                <p className="text-text-muted leading-relaxed text-sm">
                                    {displayData.ingredients}
                                </p>
                            </AccordionItem>

                            <AccordionItem title="Consigli Perla Negra">
                                <div className="bg-background-alt p-6 rounded-xl border-l-2 border-accent">
                                    <p className="text-text-muted leading-relaxed text-sm">
                                        {displayData.tips}
                                    </p>
                                </div>
                            </AccordionItem>
                        </div>
                    </Reveal>
                </div>

                {/* Related Products Section */}
                {products.filter(p => p.category === product.category && p.slug !== product.slug).length > 0 && (
                    <div className="border-t border-border/10 pt-16 pb-24 mt-16">
                        <h2 className="text-2xl font-serif text-text-primary mb-8 text-center">Potrebbe piacerti anche</h2>
                        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 lg:gap-8">
                            {products
                                .filter(p => p.category === product.category && p.slug !== product.slug)
                                .slice(0, 4)
                                .map(relatedProduct => (
                                    <div key={relatedProduct.id} className="h-full">
                                        <ProductCard product={relatedProduct} />
                                    </div>
                                ))}
                        </div>
                    </div>
                )}

            </div>

            {/* Smart Sticky Mobile Bar: Only visible on mobile (md:hidden) */}
            <AnimatePresence>
                {showStickyBar && (
                    <motion.div
                        initial={{ y: '100%' }}
                        animate={{ y: 0 }}
                        exit={{ y: '100%' }}
                        transition={{ type: 'spring', stiffness: 300, damping: 30 }}
                        className="fixed bottom-0 left-0 right-0 z-50 bg-background-alt/90 backdrop-blur-xl border-t border-white/10 p-4 pb-6 md:pb-4 shadow-[0_-10px_30px_-10px_rgba(0,0,0,0.5)] md:hidden"
                    >
                        <div className="flex items-center gap-4 max-w-7xl mx-auto">
                            {/* Product Info (Compact) */}
                            <div className="flex-1 min-w-0">
                                <h3 className="text-sm font-serif text-text-primary truncate leading-tight">{product.name}</h3>
                                <div className="text-accent font-bold text-base">€{product.price.toFixed(2)}</div>
                            </div>

                            {/* Action Button */}
                            <button
                                onClick={handleAddToCart}
                                className="bg-accent text-background-dark px-6 py-3 rounded-full font-bold text-sm shadow-[0_0_15px_rgba(63,255,193,0.3)] active:scale-95 transition-transform flex items-center gap-2"
                            >
                                <span>Aggiungi</span>
                                <ShoppingBag size={16} />
                            </button>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
};

export default ProductDetailPage;

import { useProducts } from '@/features/products/hooks/useProducts';
import { useProductFilters } from '@/features/products/hooks/useProductFilters';
import ProductCard from '@/features/products/components/ProductCard';
import ProductSearchBar from '@/features/products/components/ProductSearchBar';
import ProductFilters from '@/features/products/components/ProductFilters';
import SkeletonProductCard from '@/features/products/components/SkeletonProductCard';
import Drawer from '@/components/ui/Drawer';
import { Loader2, Filter, Search } from 'lucide-react';
import SEO from '@/components/ui/SEO';

const ProductListPage = () => {
    // Data Fetching
    const { products, loading, error } = useProducts();

    // Use Custom Hook for Filtering
    const {
        selectedCategories,
        setSelectedCategories,
        selectedSensations,
        sortOrder,
        setSortOrder,
        priceRange,
        setPriceRange,
        isFilterOpen,
        setIsFilterOpen,
        expanded,
        toggleSection,
        categories,
        sensations,
        handleCategoryChange,
        handleSensationChange,
        clearFilters,
        filteredAndSortedProducts,
        setSearchQuery,
        selectedProductFilters,
        handleProductFilterChange,
        gameTypes,
        flavorOptions,
        usageOptions,
        selectedTarget,
        handleTargetChange,
        targetAudiences
    } = useProductFilters(products);

    const filterProps = {
        clearFilters,
        sortOrder,
        setSortOrder,
        expanded,
        toggleSection,
        sensations,
        selectedSensations,
        handleSensationChange,
        priceRange,
        setPriceRange,
        selectedProductFilters,
        handleProductFilterChange,
        gameTypes,
        flavorOptions,
        usageOptions,
        selectedTarget,
        handleTargetChange,
        targetAudiences,
        selectedCategories  // Add category context for intelligent labels
    };

    return (
        <div className="bg-background-dark min-h-screen pt-24 pb-8 fade-in">
            <SEO title="Catalogo" description="Esplora la nostra selezione di prodotti premium. Lubrificanti, profumi e accessori." />
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

                {/* New Header Section - Full Width for Mobile */}
                <div className="mb-6 md:hidden">
                    {loading ? (
                        <div className="grid grid-cols-2 gap-3 mb-6">
                            {[...Array(4)].map((_, i) => (
                                <SkeletonProductCard key={i} />
                            ))}
                        </div>
                    ) : (
                        <div className="mb-6 flex flex-col gap-4 border-b border-border/10 pb-4">
                            <div>
                                <h2 className="text-3xl font-serif text-text-primary mb-2">I nostri Prodotti</h2>
                                <p className="text-text-muted text-sm">
                                    Mostrando {filteredAndSortedProducts.length} risultati
                                </p>
                            </div>
                        </div>
                    )}
                    {/* Mobile Categories - Full Width */}
                    <div className="w-full overflow-x-auto no-scrollbar">
                        <div className="flex gap-2 pb-2">
                            <button
                                onClick={() => setSelectedCategories([])}
                                className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-all ${selectedCategories.length === 0
                                    ? 'bg-accent text-background-dark shadow-[0_0_10px_rgba(63,255,193,0.3)]'
                                    : 'bg-background-alt text-text-muted border border-border/30 hover:border-accent/50 hover:text-text-primary'
                                    }`}
                            >
                                Tutti
                            </button>
                            {categories.map(cat => (
                                <button
                                    key={cat}
                                    onClick={() => handleCategoryChange(cat)}
                                    className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-all ${selectedCategories.includes(cat)
                                        ? 'bg-accent text-background-dark shadow-[0_0_10px_rgba(63,255,193,0.3)]'
                                        : 'bg-background-alt text-text-muted border border-border/30 hover:border-accent/50 hover:text-text-primary'
                                        }`}
                                >
                                    {cat}
                                </button>
                            ))}
                        </div>
                    </div>
                </div>

                {/* Mobile Search Bar - Added here to be above Filters */}
                <div className="md:hidden mt-4 mb-8">
                    <ProductSearchBar onSearch={setSearchQuery} id="product-search-mobile" />
                </div>

                {/* Mobile Filter Button - Floating Bottom Right */}
                <div className="md:hidden fixed bottom-6 right-6 z-40">
                    <button
                        onClick={() => setIsFilterOpen(true)}
                        className="bg-accent text-background-dark px-6 py-3 rounded-full font-bold shadow-lg flex items-center gap-2 hover:bg-accent-hover active:scale-95 transition-all"
                    >
                        <Filter size={20} />
                        <span>Filtri</span>
                    </button>
                </div>

                {/* Mobile Filter Drawer */}
                <Drawer
                    isOpen={isFilterOpen}
                    onClose={() => setIsFilterOpen(false)}
                    title="Designa i tuoi gusti"
                    side="bottom"
                >
                    <ProductFilters {...filterProps} isMobile={true} />
                    <div className="mt-8 pt-4 border-t border-border/10">
                        <button
                            onClick={() => setIsFilterOpen(false)}
                            className="w-full bg-accent text-background-dark py-3 rounded-xl font-bold hover:bg-accent-hover transition-colors"
                        >
                            Mostra {filteredAndSortedProducts.length} risultati
                        </button>
                    </div>
                </Drawer>


                <div className="flex flex-col md:grid md:grid-cols-[16rem_1fr] md:items-start gap-8">

                    {/* Desktop Header - Row 1, Col 2 */}
                    <div className="hidden md:block md:col-start-2 mb-8">
                        <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6 border-b border-border/10 pb-4">
                            <div>
                                <h1 className="text-3xl font-serif text-text-primary mb-2">I nostri Prodotti</h1>
                                <p className="text-text-muted text-sm">
                                    Mostrando {filteredAndSortedProducts.length} risultati
                                </p>
                            </div>
                        </div>

                        {/* Category Chips - Desktop */}
                        <div className="mb-6 overflow-x-auto scrollbar-thin scrollbar-thumb-accent/20 scrollbar-track-transparent">
                            <div className="flex gap-2 pb-2">
                                <button
                                    onClick={() => setSelectedCategories([])}
                                    className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-all ${selectedCategories.length === 0
                                        ? 'bg-accent text-background-dark shadow-[0_0_10px_rgba(63,255,193,0.3)]'
                                        : 'bg-background-alt text-text-muted border border-border/30 hover:border-accent/50 hover:text-text-primary'
                                        }`}
                                >
                                    Tutti
                                </button>
                                {categories.map(cat => (
                                    <button
                                        key={cat}
                                        onClick={() => handleCategoryChange(cat)}
                                        className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-all ${selectedCategories.includes(cat)
                                            ? 'bg-accent text-background-dark shadow-[0_0_10px_rgba(63,255,193,0.3)]'
                                            : 'bg-background-alt text-text-muted border border-border/30 hover:border-accent/50 hover:text-text-primary'
                                            }`}
                                    >
                                        {cat}
                                    </button>
                                ))}
                            </div>
                        </div>

                        {/* Search Section - Desktop */}
                        <div className="w-full sm:max-w-md">
                            <ProductSearchBar onSearch={setSearchQuery} id="product-search-desktop" />
                        </div>
                    </div>

                    {/* Sidebar Filters - Row 2, Col 1 */}
                    <aside className="hidden md:block w-full md:w-64 flex-shrink-0 md:row-start-2 h-fit">
                        <div className="bg-background-alt p-6 rounded-3xl border border-border/10 transition-all duration-300 md:sticky md:top-24">
                            <ProductFilters {...filterProps} isMobile={false} />
                        </div>
                    </aside>


                    {/* Product Grid - Row 2, Col 2 */}
                    <div className="flex-grow w-full md:col-start-2 md:row-start-2">



                        {/* // ... (other imports) */}

                        {/* // Inside component */}
                        {loading ? (
                            <div className="grid grid-cols-2 lg:grid-cols-3 gap-3 md:gap-6">
                                {[...Array(9)].map((_, i) => (
                                    <SkeletonProductCard key={i} />
                                ))}
                            </div>
                        ) : filteredAndSortedProducts.length > 0 ? (
                            <div className="grid grid-cols-2 lg:grid-cols-3 gap-3 md:gap-6">
                                {filteredAndSortedProducts.map((product) => (
                                    <div key={product.id} className="h-full">
                                        <ProductCard product={product} />
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="text-center py-20 bg-background-alt/50 rounded-lg border border-dashed border-border/20">
                                <Search className="mx-auto text-text-muted mb-4 opacity-50" size={48} />
                                <h3 className="text-xl text-text-primary font-medium mb-2">Nessun prodotto trovato</h3>
                                <p className="text-text-muted">Prova a regolare i filtri di ricerca.</p>
                                <button
                                    onClick={clearFilters}
                                    className="mt-6 text-accent hover:underline"
                                >
                                    Cancella tutti i filtri
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            </div >
        </div >
    );
};

export default ProductListPage;

import { useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { Send, MapPin, User, Mail, Phone, MessageSquare, CheckCircle } from 'lucide-react';
import SEO from '@/components/ui/SEO';
import Select from '@/components/ui/Select';
import { toast } from 'sonner';
import { isValidPhoneNumber } from 'libphonenumber-js';

const ResellerPage = () => {
    const [formData, setFormData] = useState({
        nombre: '',
        cognome: '',
        email: '',
        telefono: '+39',
        provincia: '',
        citta: '',
        conoscenza: '',
        messaggio: '',
        trap: '' // Honeypot
    });

    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);

    // Parallax Logic
    const { scrollY } = useScroll();
    const yBg = useTransform(scrollY, [0, 1000], [0, 200]);

    // Fade-in animation for content
    const containerVariants = {
        hidden: { opacity: 0, y: 20 },
        visible: {
            opacity: 1,
            y: 0,
            transition: { duration: 0.6, ease: [0.22, 1, 0.36, 1] }
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        if (errors[e.target.name]) {
            setErrors({ ...errors, [e.target.name]: null });
        }
    };

    const validateForm = () => {
        const newErrors = {};

        // Name & Surname
        if (!formData.nombre.trim()) newErrors.nombre = "Il nome è obbligatorio";
        if (!formData.cognome.trim()) newErrors.cognome = "Il cognome è obbligatorio";

        // Email
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!formData.email.trim()) {
            newErrors.email = "L'email è obbligatoria";
        } else if (!emailRegex.test(formData.email)) {
            newErrors.email = "Inserisci un'email valida";
        }

        // Phone (Same strict check as Checkout)
        if (!formData.telefono || formData.telefono.trim() === '+' || !formData.telefono.trim()) {
            newErrors.telefono = "Il telefono è obbligatorio";
        } else if (!isValidPhoneNumber(formData.telefono)) {
            newErrors.telefono = "Numero non valido (controlla prefisso).";
        }

        // Location
        if (!formData.provincia) newErrors.provincia = "Seleziona una provincia";
        if (!formData.citta.trim()) newErrors.citta = "La città è obbligatoria";

        // How did you find us
        if (!formData.conoscenza) newErrors.conoscenza = "Seleziona un'opzione";

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const [isSuccess, setIsSuccess] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            toast.error("Per favorere correggi gli errori nel modulo.", {
                style: { backgroundColor: '#fee2e2', color: '#dc2626' }
            });
            return;
        }

        setIsSubmitting(true);

        try {
            const response = await fetch("https://formsubmit.co/ajax/panteranegrait@gmail.com", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({
                    _subject: `Nuova Candidatura Rivenditore: ${formData.nombre} ${formData.cognome}`,
                    _template: "table",
                    _captcha: "false",
                    _honey: formData.trap,
                    Nome: formData.nombre,
                    Cognome: formData.cognome,
                    Email: formData.email,
                    Telefono: formData.telefono,
                    Citta: `${formData.citta} (${formData.provincia})`,
                    Conosciuto_Tramite: formData.conoscenza,
                    Messaggio: formData.messaggio
                })
            });

            if (response.ok) {
                toast.success("Candidatura inviata con successo!");
                setFormData({
                    nombre: '', cognome: '', email: '', telefono: '+39',
                    provincia: '', citta: '', conoscenza: '', messaggio: '', trap: ''
                });
                setIsSuccess(true); // Trigger success view
            } else {
                toast.error("Si è verificato un errore.", { description: "Riprova più tardi." });
            }
        } catch (error) {
            toast.error("Errore di connessione.");
        } finally {
            setIsSubmitting(false);
        }
    };

    const provinceOptions = [
        { value: 'MI', label: 'Milano' },
        { value: 'RM', label: 'Roma' },
        { value: 'NA', label: 'Napoli' },
        { value: 'TO', label: 'Torino' },
        { value: 'FI', label: 'Firenze' },
        { value: 'BO', label: 'Bologna' },
        { value: 'Altro', label: 'Altra Provincia' }
    ];

    const sourceOptions = [
        { value: 'Instagram', label: 'Instagram' },
        { value: 'Facebook', label: 'Facebook' },
        { value: 'Google', label: 'Google' },
        { value: 'Amico', label: 'Passaparola / Amico' },
        { value: 'Altro', label: 'Altro' }
    ];

    return (
        <div className="min-h-screen bg-background-dark flex flex-col lg:flex-row overflow-hidden relative pt-24">
            <SEO
                title="Diventa Rivenditore"
                description="Unisciti alla rete Perla Negra. Porta l'eleganza e la seduzione nel tuo business."
            />

            {/* Left Column: Image & Inspiration (Parallax) */}
            <div className="lg:w-1/2 relative min-h-[40vh] lg:min-h-screen overflow-hidden group">
                {/* Background Image Wrapper with Parallax */}
                <motion.div
                    style={{ y: yBg }}
                    className="absolute inset-0 h-[120%] -top-[10%]"
                >
                    <img
                        src="/hero/reseller.webp"
                        alt="Perla Negra Business"
                        className="w-full h-full object-cover opacity-60 group-hover:scale-105 transition-transform duration-1000 ease-out"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-background-dark via-background-dark/20 to-transparent lg:bg-gradient-to-r lg:from-transparent lg:to-background-dark" />
                </motion.div>

                {/* Content Overlay */}
                <div className="absolute inset-0 flex flex-col justify-end lg:justify-center p-8 lg:p-16 z-10 pointer-events-none">
                    <motion.div
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true }}
                        variants={containerVariants}
                    >
                        <h1 className="text-4xl lg:text-6xl font-serif text-white mb-6 drop-shadow-lg">
                            Inizia il tuo <br />
                            <span className="text-accent italic">Business</span>
                        </h1>
                        <p className="text-text-muted text-lg lg:text-xl max-w-md leading-relaxed mb-8">
                            Unisciti alle migliaia di imprenditori che ci scelgono, potenziando il loro business e moltiplicando i profitti con l'eleganza di Perla Negra.
                        </p>
                        <div className="hidden lg:flex items-center gap-4 text-sm text-white/50 uppercase tracking-widest">
                            <span>Esclusività</span>
                            <span className="w-1 h-1 bg-accent rounded-full" />
                            <span>Qualità</span>
                            <span className="w-1 h-1 bg-accent rounded-full" />
                            <span>Supporto</span>
                        </div>
                    </motion.div>
                </div>
            </div>

            {/* Right Column: Form */}
            <div className="lg:w-1/2 p-4 lg:p-12 flex items-center justify-center overflow-y-auto">
                <div className="w-full max-w-lg pt-16 lg:pt-0">
                    <div className="mb-8">
                        <h2 className="text-3xl font-serif text-white mb-2">Diventa Partner</h2>
                        <p className="text-text-muted">Compila il modulo per candidarti come rivenditore ufficiale.</p>
                    </div>

                    {isSuccess ? (
                        <motion.div
                            initial={{ opacity: 0, scale: 0.95 }}
                            animate={{ opacity: 1, scale: 1 }}
                            className="bg-background-alt/50 backdrop-blur-md p-10 rounded-3xl border border-white/5 text-center"
                        >
                            <div className="w-20 h-20 bg-accent/20 rounded-full flex items-center justify-center mx-auto mb-6 text-accent">
                                <CheckCircle size={40} />
                            </div>
                            <h3 className="text-2xl font-serif text-white mb-4">Candidatura Inviata!</h3>
                            <p className="text-text-muted mb-8 leading-relaxed">
                                Grazie per il tuo interesse. Il nostro team esaminerà la tua richiesta e ti contatterà al più presto per discutere le opportunità di collaborazione.
                            </p>
                            <button
                                onClick={() => setIsSuccess(false)}
                                className="px-8 py-3 bg-white/5 hover:bg-white/10 rounded-full text-white text-sm font-bold tracking-wider transition-colors"
                            >
                                TORNA AL MODULO
                            </button>
                        </motion.div>
                    ) : (
                        <form onSubmit={handleSubmit} className="space-y-5 bg-background-alt/30 p-8 rounded-3xl backdrop-blur-sm border border-white/5 shadow-2xl">
                            {/* ... Existing Form Fields ... */}
                            <div className="grid grid-cols-2 gap-4">
                                <div>
                                    <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Nome</label>
                                    <div className="relative">
                                        <User className="absolute left-4 top-1/2 -translate-y-1/2 text-text-muted w-4 h-4" />
                                        <input
                                            type="text"
                                            name="nombre"
                                            value={formData.nombre}
                                            onChange={handleInputChange}
                                            className={`w-full bg-background-dark border ${errors.nombre ? 'border-red-500' : 'border-white/10'} rounded-xl pl-10 pr-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                            placeholder="Mario"
                                        />
                                    </div>
                                    {errors.nombre && <p className="text-red-500 text-xs mt-1 ml-2">{errors.nombre}</p>}
                                </div>
                                <div>
                                    <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Cognome</label>
                                    <input
                                        type="text"
                                        name="cognome"
                                        value={formData.cognome}
                                        onChange={handleInputChange}
                                        className={`w-full bg-background-dark border ${errors.cognome ? 'border-red-500' : 'border-white/10'} rounded-xl px-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                        placeholder="Rossi"
                                    />
                                    {errors.cognome && <p className="text-red-500 text-xs mt-1 ml-2">{errors.cognome}</p>}
                                </div>
                            </div>

                            {/* Contact Info */}
                            <div className="space-y-4">
                                <div>
                                    <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Email</label>
                                    <div className="relative">
                                        <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-text-muted w-4 h-4" />
                                        <input
                                            type="email"
                                            name="email"
                                            value={formData.email}
                                            onChange={handleInputChange}
                                            className={`w-full bg-background-dark border ${errors.email ? 'border-red-500' : 'border-white/10'} rounded-xl pl-10 pr-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                            placeholder="mario@azienda.com"
                                        />
                                    </div>
                                    {errors.email && <p className="text-red-500 text-xs mt-1 ml-2">{errors.email}</p>}
                                </div>

                                <div>
                                    <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Telefono</label>
                                    <div className="relative">
                                        <Phone className="absolute left-4 top-1/2 -translate-y-1/2 text-text-muted w-4 h-4" />
                                        <input
                                            type="tel"
                                            name="telefono"
                                            value={formData.telefono}
                                            onChange={handleInputChange}
                                            className={`w-full bg-background-dark border ${errors.telefono ? 'border-red-500' : 'border-white/10'} rounded-xl pl-10 pr-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                            placeholder="+39 333 1234567"
                                        />
                                    </div>
                                    {errors.telefono && <p className="text-red-500 text-xs mt-1 ml-2">{errors.telefono}</p>}
                                </div>
                            </div>

                            {/* Location */}
                            <div className="grid grid-cols-2 gap-4">
                                <div>
                                    <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Provincia</label>
                                    <div className="relative">
                                        <MapPin className="absolute left-4 top-1/2 -translate-y-1/2 text-text-muted w-4 h-4 z-10" />
                                        <Select
                                            options={provinceOptions}
                                            value={formData.provincia}
                                            onChange={(val) => {
                                                setFormData({ ...formData, provincia: val });
                                                if (errors.provincia) setErrors({ ...errors, provincia: null });
                                            }}
                                            placeholder="Seleziona"
                                            className={`w-full bg-background-dark border ${errors.provincia ? 'border-red-500' : 'border-white/10'} rounded-xl pl-10 pr-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                        />
                                    </div>
                                    {errors.provincia && <p className="text-red-500 text-xs mt-1 ml-2">{errors.provincia}</p>}
                                </div>
                                <div>
                                    <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Città</label>
                                    <input
                                        type="text"
                                        name="citta"
                                        value={formData.citta}
                                        onChange={handleInputChange}
                                        className={`w-full bg-background-dark border ${errors.citta ? 'border-red-500' : 'border-white/10'} rounded-xl px-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                        placeholder="Milano"
                                    />
                                    {errors.citta && <p className="text-red-500 text-xs mt-1 ml-2">{errors.citta}</p>}
                                </div>
                            </div>

                            {/* Source */}
                            <div>
                                <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Come ci hai conosciuto?</label>
                                <Select
                                    options={sourceOptions}
                                    value={formData.conoscenza}
                                    onChange={(val) => {
                                        setFormData({ ...formData, conoscenza: val });
                                        if (errors.conoscenza) setErrors({ ...errors, conoscenza: null });
                                    }}
                                    placeholder="Seleziona un'opzione"
                                    className={`w-full bg-background-dark border ${errors.conoscenza ? 'border-red-500' : 'border-white/10'} rounded-xl px-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all`}
                                />
                                {errors.conoscenza && <p className="text-red-500 text-xs mt-1 ml-2">{errors.conoscenza}</p>}
                            </div>

                            {/* Message */}
                            <div>
                                <label className="text-xs text-text-muted ml-2 uppercase tracking-wider font-bold">Messaggio / Note</label>
                                <div className="relative">
                                    <MessageSquare className="absolute left-4 top-4 text-text-muted w-4 h-4" />
                                    <textarea
                                        name="messaggio"
                                        rows="3"
                                        value={formData.messaggio}
                                        onChange={handleInputChange}
                                        className="w-full bg-background-dark border border-white/10 rounded-xl pl-10 pr-4 py-3 text-white hover:border-accent/30 focus:ring-1 focus:ring-accent/50 focus:border-accent focus:outline-none transition-all resize-none"
                                        placeholder="Raccontaci del tuo business..."
                                    ></textarea>
                                </div>
                            </div>

                            {/* Honeypot Trap - Invisible to humans */}
                            <input
                                type="text"
                                name="trap"
                                value={formData.trap}
                                onChange={handleInputChange}
                                style={{ display: 'none' }}
                                tabIndex="-1"
                                autoComplete="off"
                            />

                            {/* Submit Button */}
                            <div className="space-y-2">
                                <motion.button
                                    whileHover={{ scale: 1.02 }}
                                    whileTap={{ scale: 0.98 }}
                                    type="submit"
                                    disabled={isSubmitting}
                                    className={`w-full bg-accent text-background-dark font-bold py-4 rounded-xl shadow-lg shadow-accent/20 flex items-center justify-center gap-2 uppercase tracking-widest ${isSubmitting ? 'opacity-50 cursor-not-allowed' : 'hover:bg-accent-hover'}`}
                                >
                                    {isSubmitting ? (
                                        <span>Invio in corso...</span>
                                    ) : (
                                        <>
                                            <Send size={18} />
                                            <span>Invia Candidatura</span>
                                        </>
                                    )}
                                </motion.button>
                            </div>

                            <p className="text-center text-xs text-text-muted mt-4">
                                Ti contatteremo al più presto.
                            </p>
                        </form>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ResellerPage;

import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Save, ArrowLeft, Loader2, Calendar } from 'lucide-react';
import { supabase } from '@/lib/supabase';
import { toast } from 'sonner';

const AdminCouponForm = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const isEditing = Boolean(id);

    const [loading, setLoading] = useState(false);
    const [initialLoading, setInitialLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        code: '',
        discount_type: 'percent', // DB expects 'percent', not 'percentage'
        discount_value: '',
        expiration_date: '',
        usage_limit: '',
        min_purchase_amount: ''
    });

    useEffect(() => {
        if (isEditing) {
            fetchCoupon();
        }
    }, [id]);

    const fetchCoupon = async () => {
        try {
            const { data, error } = await supabase
                .from('coupons')
                .select('*')
                .eq('id', id)
                .single();

            if (error) throw error;

            // Format date for input: YYYY-MM-DD
            let formattedDate = '';
            // Handle expiration_date OR expires_at
            const dbDate = data.expiration_date || data.expires_at;
            if (dbDate) {
                formattedDate = new Date(dbDate).toISOString().split('T')[0];
            }

            setFormData({
                code: data.code,
                discount_type: data.discount_type, // Now using 'percent' consistently
                discount_value: data.discount_value ?? data.value,
                expiration_date: formattedDate,
                usage_limit: data.usage_limit || '',
                min_purchase_amount: data.min_purchase_amount || ''
            });

        } catch (error) {
            console.error('Error fetching coupon:', error);
            toast.error('Error al cargar datos del cupón');
            navigate('/admin/coupons');
        } finally {
            setInitialLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name === 'code') {
            setFormData(prev => ({ ...prev, [name]: value.toUpperCase().replace(/\s/g, '') }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const couponData = {
            code: formData.code,
            discount_type: formData.discount_type,
            value: parseFloat(formData.discount_value), // DB column is 'value'
            expires_at: formData.expiration_date || null, // DB column is 'expires_at'
            usage_limit: formData.usage_limit ? parseInt(formData.usage_limit) : null,
            min_purchase_amount: formData.min_purchase_amount ? parseFloat(formData.min_purchase_amount) : 0,
            active: true // DB column is 'active'
        };

        try {
            let error;

            if (isEditing) {
                // Update
                const { error: updateError } = await supabase
                    .from('coupons')
                    .update(couponData)
                    .eq('id', id);
                error = updateError;
            } else {
                // Create
                const { error: insertError } = await supabase
                    .from('coupons')
                    .insert([couponData]);
                error = insertError;
            }

            if (error) throw error;

            toast.success(`Cupón ${isEditing ? 'actualizado' : 'creado'} exitosamente`);
            navigate('/admin/coupons');

        } catch (error) {
            console.error('Error saving coupon:', error);
            if (error.code === '23505') { // Unique violation
                toast.error('Error: El código de cupón ya existe');
            } else {
                toast.error(`Error: ${error.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    if (initialLoading) {
        return (
            <div className="flex items-center justify-center min-h-[400px]">
                <Loader2 className="animate-spin text-accent" size={40} />
            </div>
        );
    }

    return (
        <div className="max-w-2xl mx-auto pb-12">
            {/* Header */}
            <div className="flex items-center gap-4 mb-8">
                <button
                    onClick={() => navigate('/admin/coupons')}
                    className="p-2 bg-white/5 rounded-full hover:bg-white/10 transition-colors text-white"
                >
                    <ArrowLeft size={20} />
                </button>
                <div>
                    <h1 className="text-3xl font-playfair font-bold text-white">{isEditing ? 'Editar Cupón' : 'Nuevo Cupón'}</h1>
                    <p className="text-text-muted">{isEditing ? 'Modifica los detalles del cupón' : 'Crea un código promocional para tus clientes'}</p>
                </div>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
                <div className="bg-white/5 border border-white/10 rounded-2xl p-6 backdrop-blur-md space-y-6">

                    {/* Code */}
                    <div className="space-y-2">
                        <label className="text-sm text-text-muted">Código del Cupón *</label>
                        <input
                            required
                            type="text"
                            name="code"
                            value={formData.code}
                            onChange={handleChange}
                            className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50 font-mono tracking-wider uppercase text-lg"
                            placeholder="EJ: VERANO2025"
                        />
                        <p className="text-xs text-text-muted">Se convertirá automáticamente a mayúsculas y sin espacios.</p>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Type */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Tipo de Descuento</label>
                            <select
                                name="discount_type"
                                value={formData.discount_type}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white [&>option]:bg-zinc-900"
                            >
                                <option value="percent">Porcentaje (%)</option>
                                <option value="fixed">Monto Fijo ($)</option>
                            </select>
                        </div>

                        {/* Value */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Valor *</label>
                            <input
                                required
                                type="number"
                                min="0"
                                step={formData.discount_type === 'percent' ? '1' : '0.01'}
                                max={formData.discount_type === 'percent' ? '100' : undefined}
                                name="discount_value"
                                value={formData.discount_value}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white"
                                placeholder={formData.discount_type === 'percent' ? '15' : '500.00'}
                            />
                        </div>
                    </div>

                    {/* Expiration */}
                    <div className="space-y-2">
                        <label className="text-sm text-text-muted">Fecha de Expiración (Opcional)</label>
                        <div className="relative">
                            <Calendar className="absolute left-4 top-1/2 -translate-y-1/2 text-text-muted" size={18} />
                            <input
                                type="date"
                                name="expiration_date"
                                value={formData.expiration_date}
                                onChange={handleChange}
                                className="w-full pl-12 pr-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white [color-scheme:dark]"
                            />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Usage Limit */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Límite de Usos (Opcional)</label>
                            <input
                                type="number"
                                min="1"
                                name="usage_limit"
                                value={formData.usage_limit}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white"
                                placeholder="Ej: 100"
                            />
                            <p className="text-xs text-text-muted">Dejar vacío para uso ilimitado.</p>
                        </div>

                        {/* Min Purchase */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Mínimo de Compra ($)</label>
                            <input
                                type="number"
                                min="0"
                                name="min_purchase_amount"
                                value={formData.min_purchase_amount}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white"
                                placeholder="0.00"
                            />
                        </div>
                    </div>

                </div>

                {/* Actions */}
                <div className="flex gap-4">
                    <button
                        type="button"
                        onClick={() => navigate('/admin/coupons')}
                        className="flex-1 py-3 px-4 rounded-xl border border-white/10 text-white font-medium hover:bg-white/5 transition-colors"
                    >
                        Cancelar
                    </button>
                    <button
                        type="submit"
                        disabled={loading}
                        className="flex-1 py-3 px-4 rounded-xl bg-accent text-background-dark font-bold hover:bg-accent-light transition-colors shadow-lg shadow-accent/20 flex items-center justify-center gap-2"
                    >
                        {loading ? (
                            <Loader2 className="animate-spin" size={20} />
                        ) : (
                            <>
                                <Save size={20} />
                                {isEditing ? 'Actualizar Cupón' : 'Crear Cupón'}
                            </>
                        )}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default AdminCouponForm;

