import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '@/features/auth/context/AuthContext';
import { supabase } from '@/lib/supabase';
import { getOrderStats } from '@/features/orders/services/orderService';

const AdminDashboard = () => {
    const { user } = useAuth();
    const [stats, setStats] = useState({
        activeProducts: 0,
        activeCoupons: 0,
        totalSales: 0,
        recentOrders: 0,
        loading: true
    });

    useEffect(() => {
        fetchStats();
    }, []);

    const fetchStats = async () => {
        try {
            // Fetch all products count (no 'available' column exists)
            const { count: productCount, error: productError } = await supabase
                .from('products')
                .select('*', { count: 'exact', head: true });

            // Fetch active coupons count
            const { count: couponCount, error: couponError } = await supabase
                .from('coupons')
                .select('*', { count: 'exact', head: true })
                .eq('active', true);

            // Fetch order statistics
            const orderStats = await getOrderStats();

            if (!productError && !couponError) {
                setStats({
                    activeProducts: productCount || 0,
                    activeCoupons: couponCount || 0,
                    totalSales: orderStats.totalSales || 0,
                    recentOrders: orderStats.recentOrdersCount || 0,
                    loading: false
                });
            }
        } catch (error) {
            console.error('Error fetching stats:', error);
            setStats(prev => ({ ...prev, loading: false }));
        }
    };

    return (
        <div className="px-6 py-8 space-y-8">
            {/* Header */}
            <div className="space-y-4">
                <Link
                    to="/"
                    className="inline-flex items-center gap-2 text-text-muted hover:text-accent transition-colors group mb-2"
                >
                    <span className="group-hover:-translate-x-1 transition-transform">←</span>
                    <span className="text-sm font-medium">Volver al Sitio Público</span>
                </Link>
                <div>
                    <h1 className="text-3xl font-bold text-white mb-2">Bienvenido, {user?.email?.split('@')[0]} 👋</h1>
                    <p className="text-text-muted">Aquí tienes un resumen de tu tienda hoy.</p>
                </div>
            </div>

            {/* Quick Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {/* Products Card */}
                <Link
                    to="/admin/products"
                    className="bg-[#141414] border border-white/10 rounded-xl p-6 hover:border-[#3FFFC1]/30 transition-all group cursor-pointer"
                >
                    <div className="flex justify-between items-start mb-4">
                        <div className="p-3 bg-purple-500/10 rounded-lg group-hover:bg-purple-500/20 transition-colors">
                            <span className="text-2xl">📦</span>
                        </div>
                        <span className="text-xs text-text-muted bg-white/5 py-1 px-2 rounded-full">Total</span>
                    </div>
                    <h3 className="text-3xl font-bold text-white mb-1">
                        {stats.loading ? '--' : stats.activeProducts}
                    </h3>
                    <p className="text-sm text-text-muted">Productos</p>
                </Link>

                {/* Orders Card */}
                <Link
                    to="/admin/orders"
                    className="bg-[#141414] border border-white/10 rounded-xl p-6 hover:border-[#3FFFC1]/30 transition-all group cursor-pointer"
                >
                    <div className="flex justify-between items-start mb-4">
                        <div className="p-3 bg-blue-500/10 rounded-lg group-hover:bg-blue-500/20 transition-colors">
                            <span className="text-2xl">📋</span>
                        </div>
                        <span className="text-xs text-text-muted bg-white/5 py-1 px-2 rounded-full">30 días</span>
                    </div>
                    <h3 className="text-3xl font-bold text-white mb-1">
                        {stats.loading ? '--' : stats.recentOrders}
                    </h3>
                    <p className="text-sm text-text-muted">Pedidos</p>
                </Link>

                {/* Sales Card */}
                <Link
                    to="/admin/orders?status=completada"
                    className="bg-[#141414] border border-white/10 rounded-xl p-6 hover:border-[#3FFFC1]/30 transition-all group cursor-pointer"
                >
                    <div className="flex justify-between items-start mb-4">
                        <div className="p-3 bg-green-500/10 rounded-lg group-hover:bg-green-500/20 transition-colors">
                            <span className="text-2xl">💰</span>
                        </div>
                        <span className="text-xs text-text-muted bg-white/5 py-1 px-2 rounded-full">Completadas</span>
                    </div>
                    <h3 className="text-3xl font-bold text-white mb-1">
                        {stats.loading ? '--' : `€${stats.totalSales.toFixed(2)}`}
                    </h3>
                    <p className="text-sm text-text-muted">Ventas Totales</p>
                </Link>

                {/* Coupons Card */}
                <Link
                    to="/admin/coupons"
                    className="bg-[#141414] border border-white/10 rounded-xl p-6 hover:border-[#3FFFC1]/30 transition-all group cursor-pointer"
                >
                    <div className="flex justify-between items-start mb-4">
                        <div className="p-3 bg-pink-500/10 rounded-lg group-hover:bg-pink-500/20 transition-colors">
                            <span className="text-2xl">🎫</span>
                        </div>
                        <span className="text-xs text-text-muted bg-white/5 py-1 px-2 rounded-full">Activos</span>
                    </div>
                    <h3 className="text-3xl font-bold text-white mb-1">
                        {stats.loading ? '--' : stats.activeCoupons}
                    </h3>
                    <p className="text-sm text-text-muted">Cupones</p>
                </Link>
            </div>

            {/* Quick Actions */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
                <div className="bg-[#141414]/50 border border-white/10 rounded-xl p-6">
                    <h3 className="text-lg font-bold text-white mb-4">Atajos Rápidos</h3>
                    <div className="space-y-3">
                        <Link to="/admin/products/new" className="w-full flex items-center justify-between p-4 bg-white/5 rounded-lg hover:bg-[#3FFFC1]/10 hover:text-[#3FFFC1] transition-all group">
                            <span className="font-medium">Agregar Nuevo Producto ➕</span>
                            <span className="text-white/20 group-hover:text-[#3FFFC1]">→</span>
                        </Link>
                        <Link to="/admin/coupons/new" className="w-full flex items-center justify-between p-4 bg-white/5 rounded-lg hover:bg-[#3FFFC1]/10 hover:text-[#3FFFC1] transition-all group">
                            <span className="font-medium">Crear Cupón de Descuento 🏷️</span>
                            <span className="text-white/20 group-hover:text-[#3FFFC1]">→</span>
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, Package, Eye, Filter, Calendar, DollarSign, Trash2 } from 'lucide-react';
import { toast } from 'sonner';
import { Link } from 'react-router-dom';
import { getOrders, deleteOrder } from '@/features/orders/services/orderService';
import OrderDetailModal from '@/components/admin/OrderDetailModal';

const AdminOrderList = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        fetchOrders();
    }, [statusFilter]);

    const fetchOrders = async () => {
        try {
            setLoading(true);
            const filters = statusFilter !== 'all' ? { status: statusFilter } : {};
            const data = await getOrders(filters);
            setOrders(data || []);
        } catch (error) {
            console.error('Error fetching orders:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleViewOrder = (order) => {
        setSelectedOrder(order);
        setIsModalOpen(true);
    };

    const handleOrderUpdate = () => {
        // Refresh list after status change
        fetchOrders();
        setIsModalOpen(false);
    };

    const handleDeleteOrder = async (orderId) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar este pedido? Esta acción borrará permanentemente el registro y sus items. No se puede deshacer.')) {
            try {
                // Optimistic UI or just loader? Let's use loader for safety
                setLoading(true);
                await deleteOrder(orderId);
                toast.success('Pedido eliminado correctamente');
                await fetchOrders(); // Reload list
            } catch (error) {
                console.error('Error deleting order:', error);
                toast.error('Error al eliminar el pedido: ' + error.message);
                setLoading(false); // Only set false here if fetchOrders fails or wasn't called
            }
        }
    };

    const filteredOrders = orders.filter(order =>
        order.order_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.customer_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.customer_phone.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('it-IT', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    const getStatusBadge = (status) => {
        const statusConfig = {
            nueva: { label: 'Nueva', color: 'bg-blue-500/20 text-blue-400 border-blue-500/30' },
            en_preparacion: { label: 'En Preparación', color: 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30' },
            completada: { label: 'Completada', color: 'bg-green-500/20 text-green-400 border-green-500/30' },
            cancelada: { label: 'Cancelada', color: 'bg-red-500/20 text-red-400 border-red-500/30' }
        };

        const config = statusConfig[status] || statusConfig.nueva;

        return (
            <span className={`px-3 py-1 rounded-full text-xs font-medium border ${config.color}`}>
                {config.label}
            </span>
        );
    };

    return (
        <div className="px-6 py-8 space-y-6 text-text-primary">
            {/* Back Button */}
            <Link
                to="/admin"
                className="inline-flex items-center gap-2 text-text-muted hover:text-accent transition-colors group mb-4"
            >
                <svg className="w-5 h-5 transform group-hover:-translate-x-1 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
                <span className="font-medium">Volver al Panel</span>
            </Link>

            {/* Header */}
            <div className="space-y-4">
                <div>
                    <h1 className="text-3xl font-playfair font-bold text-white mb-2">Pedidos</h1>
                    <p className="text-text-muted">Gestiona el historial de pedidos y su estado</p>
                </div>

                {/* Filters Row */}
                <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
                    {/* Search Bar */}
                    <div className="relative w-full sm:max-w-md">
                        <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-text-muted" size={20} />
                        <input
                            type="text"
                            placeholder="Buscar por # pedido, cliente o teléfono..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full pl-12 pr-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent/50 text-white placeholder-text-muted transition-colors backdrop-blur-sm"
                        />
                    </div>

                    {/* Status Filter */}
                    <div className="flex items-center gap-2">
                        <Filter size={18} className="text-text-muted" />
                        <select
                            value={statusFilter}
                            onChange={(e) => setStatusFilter(e.target.value)}
                            className="bg-white/5 border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-accent/50 transition-colors cursor-pointer"
                        >
                            <option value="all">Todos los estados</option>
                            <option value="nueva">Nueva</option>
                            <option value="en_preparacion">En Preparación</option>
                            <option value="completada">Completada</option>
                            <option value="cancelada">Cancelada</option>
                        </select>
                    </div>
                </div>
            </div>

            {/* Orders Table */}
            <div className="bg-white/5 border border-white/10 rounded-2xl overflow-hidden backdrop-blur-md">
                <div className="overflow-x-auto">
                    <table className="w-full text-left">
                        <thead className="bg-white/5 text-text-muted text-sm uppercase tracking-wider">
                            <tr>
                                <th className="px-6 py-4 font-medium"># Pedido</th>
                                <th className="px-6 py-4 font-medium">Cliente</th>
                                <th className="px-6 py-4 font-medium">Fecha</th>
                                <th className="px-6 py-4 font-medium text-right">Total</th>
                                <th className="px-6 py-4 font-medium">Estado</th>
                                <th className="px-6 py-4 font-medium text-center">Acciones</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-white/5">
                            {loading ? (
                                // Skeleton Loading
                                [...Array(5)].map((_, i) => (
                                    <tr key={i} className="animate-pulse">
                                        <td className="px-6 py-4"><div className="h-4 w-24 bg-white/5 rounded"></div></td>
                                        <td className="px-6 py-4"><div className="h-4 w-32 bg-white/5 rounded"></div></td>
                                        <td className="px-6 py-4"><div className="h-4 w-28 bg-white/5 rounded"></div></td>
                                        <td className="px-6 py-4"><div className="h-4 w-16 bg-white/5 rounded ml-auto"></div></td>
                                        <td className="px-6 py-4"><div className="h-6 w-24 bg-white/5 rounded-full"></div></td>
                                        <td className="px-6 py-4"><div className="h-8 w-20 bg-white/5 rounded mx-auto"></div></td>
                                    </tr>
                                ))
                            ) : filteredOrders.length > 0 ? (
                                filteredOrders.map((order) => (
                                    <motion.tr
                                        key={order.id}
                                        initial={{ opacity: 0 }}
                                        animate={{ opacity: 1 }}
                                        className="hover:bg-white/5 transition-colors group"
                                    >
                                        <td className="px-6 py-4">
                                            <span className="font-mono text-accent font-medium">
                                                {order.order_number}
                                            </span>
                                        </td>
                                        <td className="px-6 py-4">
                                            <div>
                                                <p className="text-white font-medium">{order.customer_name}</p>
                                                <p className="text-text-muted text-sm">{order.customer_phone}</p>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4 text-text-muted text-sm">
                                            {formatDate(order.created_at)}
                                        </td>
                                        <td className="px-6 py-4 text-right">
                                            <span className="text-white font-bold text-lg">
                                                €{parseFloat(order.total).toFixed(2)}
                                            </span>
                                            {order.discount_amount > 0 && (
                                                <p className="text-accent text-xs">
                                                    -{parseFloat(order.discount_amount).toFixed(2)}€
                                                </p>
                                            )}
                                        </td>
                                        <td className="px-6 py-4">
                                            {getStatusBadge(order.status)}
                                        </td>
                                        <td className="px-6 py-4 text-center">
                                            <button
                                                onClick={() => handleViewOrder(order)}
                                                className="inline-flex items-center gap-2 px-4 py-2 bg-white/5 hover:bg-accent/10 text-white hover:text-accent rounded-lg transition-colors"
                                                title="Ver detalles"
                                            >
                                                <Eye size={16} />
                                                <span className="text-sm font-medium">Ver</span>
                                            </button>
                                            <button
                                                onClick={() => handleDeleteOrder(order.id)}
                                                className="inline-flex items-center gap-2 px-4 py-2 bg-red-500/10 hover:bg-red-500/20 text-red-400 rounded-lg transition-colors ml-2"
                                                title="Eliminar pedido"
                                            >
                                                <Trash2 size={16} />
                                            </button>
                                        </td>
                                    </motion.tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="6" className="px-6 py-12 text-center text-text-muted">
                                        <Package size={48} className="mx-auto mb-4 opacity-30" />
                                        <p>No se encontraron pedidos.</p>
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Stats Summary */}
            {!loading && orders.length > 0 && (
                <div className="grid grid-cols-1 sm:grid-cols-4 gap-4 mt-6">
                    {['nueva', 'en_preparacion', 'completada', 'cancelada'].map(status => {
                        const count = orders.filter(o => o.status === status).length;
                        const total = orders
                            .filter(o => o.status === status)
                            .reduce((sum, o) => sum + parseFloat(o.total), 0);

                        return (
                            <div key={status} className="bg-background-alt/50 border border-white/10 rounded-xl p-4">
                                <p className="text-text-muted text-xs uppercase mb-1">
                                    {status === 'nueva' && 'Nuevas'}
                                    {status === 'en_preparacion' && 'En Preparación'}
                                    {status === 'completada' && 'Completadas'}
                                    {status === 'cancelada' && 'Canceladas'}
                                </p>
                                <p className="text-white text-2xl font-bold">{count}</p>
                                {status === 'completada' && (
                                    <p className="text-accent text-sm mt-1">€{total.toFixed(2)}</p>
                                )}
                            </div>
                        );
                    })}
                </div>
            )}

            {/* Order Detail Modal */}
            <OrderDetailModal
                order={selectedOrder}
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onUpdate={handleOrderUpdate}
            />
        </div>
    );
};

export default AdminOrderList;

import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Save, X, Upload, Image as ImageIcon, Loader2, ArrowLeft } from 'lucide-react';
import { supabase } from '@/lib/supabase';
import { toast } from 'sonner';

const AdminProductForm = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const isEditing = !!id;

    const [loading, setLoading] = useState(false);
    const [fetching, setFetching] = useState(isEditing);
    const [uploading, setUploading] = useState(false);

    const [formData, setFormData] = useState({
        name: '',
        subtitle: '',
        description: '',
        description_additional: '', // Internal name mapping for DB
        price: '',
        category: '', // e.g. 'Lubricantes', 'Juguetes', 'Lenceria'
        stock: '',
        image: '',
        brand: '',
        code: '',
        size: '', // e.g. "100ml" or "M/L"
        usage: '',
        ingredients: '',
        tips: '',
        sensation: ''
    });

    const [imageFile, setImageFile] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);

    // Categories hardcoded for now, could be dynamic later
    const CATEGORIES = ['Lubricantes', 'Juguetes', 'Lenceria', 'Aceites', 'Fragancias', 'Vigorizzanti', 'Olio commestibile', 'Gioco', 'Kits'];

    const convertToWebP = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = (event) => {
                const img = new Image();
                img.src = event.target.result;
                img.onload = () => {
                    const canvas = document.createElement('canvas');
                    canvas.width = img.width;
                    canvas.height = img.height;
                    const ctx = canvas.getContext('2d');
                    ctx.drawImage(img, 0, 0);

                    canvas.toBlob((blob) => {
                        if (blob) {
                            // Create a new file with .webp extension
                            const newFile = new File([blob], file.name.replace(/\.[^/.]+$/, "") + ".webp", {
                                type: 'image/webp'
                            });
                            resolve(newFile);
                        } else {
                            reject(new Error('Conversion failed'));
                        }
                    }, 'image/webp', 0.85); // 0.85 quality is a good balance
                };
            };
            reader.onerror = (error) => reject(error);
        });
    };

    useEffect(() => {
        if (isEditing) {
            fetchProduct();
        }
    }, [id]);

    const fetchProduct = async () => {
        try {
            const { data, error } = await supabase
                .from('products')
                .select('*')
                .eq('id', id)
                .single();

            if (error) throw error;

            setFormData({
                name: data.name || '',
                subtitle: data.subtitle || '',
                description: data.description || '',
                description_additional: data.description_additional || '', // Map DB field
                price: data.price || '',
                category: data.category || '',
                stock: data.stock || 0,
                image: data.image || '',
                brand: data.brand || '',
                code: data.code || '',
                size: data.size || '', // Assuming 'size' column exists, check schema later if needed
                usage: data.usage || '',
                ingredients: data.ingredients || '',
                tips: data.tips || '',
                sensation: data.sensation || ''
            });
            setImagePreview(data.image);
        } catch (error) {
            console.error('Error fetching product:', error);
            toast.error('Error al cargar el producto');
            navigate('/admin/products');
        } finally {
            setFetching(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleImageChange = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        try {
            // Show preview immediately
            setImagePreview(URL.createObjectURL(file));

            // Convert to WebP in background
            const webpFile = await convertToWebP(file);
            console.log(`Converted ${file.name} (${(file.size / 1024).toFixed(2)}KB) to ${webpFile.name} (${(webpFile.size / 1024).toFixed(2)}KB)`);
            setImageFile(webpFile);

            toast.success('Imagen optimizada automáticamente a WebP ✨');
        } catch (error) {
            console.error('Error converting image:', error);
            toast.error('No se pudo optimizar la imagen, se usará la original');
            setImageFile(file); // Fallback to original
        }
    };

    const uploadImage = async () => {
        if (!imageFile) return formData.image;

        const fileName = `${Math.random().toString(36).substring(2)}.webp`; // Always .webp
        const filePath = `${fileName}`;

        try {
            setUploading(true);
            const { error: uploadError } = await supabase.storage
                .from('products')
                .upload(filePath, imageFile);

            if (uploadError) throw uploadError;

            const { data: { publicUrl } } = supabase.storage
                .from('products')
                .getPublicUrl(filePath);

            return publicUrl;
        } catch (error) {
            console.error('Error uploading image:', error);
            throw new Error('Falló la subida de imagen');
        } finally {
            setUploading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            // 1. Upload Image if changed
            let imageUrl = formData.image;
            if (imageFile) {
                imageUrl = await uploadImage();
            }

            // 2. Prepare Data
            const productData = {
                ...formData,
                image: imageUrl,
                price: parseFloat(formData.price),
                stock: parseInt(formData.stock),
                // Generate slug from name if new
                ...(!isEditing && { slug: formData.name.toLowerCase().replace(/ /g, '-').replace(/[^\w-]+/g, '') })
            };

            // 3. Save to DB
            if (isEditing) {
                const { error } = await supabase
                    .from('products')
                    .update(productData)
                    .eq('id', id);
                if (error) throw error;
                toast.success('Producto actualizado exitosamente');
            } else {
                const { error } = await supabase
                    .from('products')
                    .insert([productData]);
                if (error) throw error;
                toast.success('Producto creado exitosamente');
            }

            navigate('/admin/products');

        } catch (error) {
            console.error('Error saving product:', error);
            toast.error(`Error: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    if (fetching) {
        return (
            <div className="flex items-center justify-center min-h-[60vh]">
                <Loader2 className="animate-spin text-accent" size={40} />
            </div>
        );
    }

    return (
        <div className="max-w-4xl mx-auto pb-12">
            {/* Header */}
            <div className="flex items-center gap-4 mb-8">
                <button
                    onClick={() => navigate('/admin/products')}
                    className="p-2 bg-white/5 rounded-full hover:bg-white/10 transition-colors text-white"
                >
                    <ArrowLeft size={20} />
                </button>
                <div>
                    <h1 className="text-3xl font-playfair font-bold text-white">
                        {isEditing ? 'Editar Producto' : 'Nuevo Producto'}
                    </h1>
                    <p className="text-text-muted">
                        {isEditing ? `Editando: ${formData.name}` : 'Agrega un nuevo producto al catálogo'}
                    </p>
                </div>
            </div>

            <form onSubmit={handleSubmit} className="space-y-8">
                {/* Main Info Card */}
                <div className="bg-white/5 border border-white/10 rounded-2xl p-6 backdrop-blur-md space-y-6">
                    <h2 className="text-xl font-medium text-white border-b border-white/10 pb-4">Información Principal</h2>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Name */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Nombre del Producto *</label>
                            <input
                                required
                                type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50"
                                placeholder="Ej: Aceite de Masaje"
                            />
                        </div>

                        {/* Category */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Categoría *</label>
                            <select
                                required
                                name="category"
                                value={formData.category}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white [&>option]:bg-zinc-900"
                            >
                                <option value="">Seleccionar...</option>
                                {CATEGORIES.map(cat => (
                                    <option key={cat} value={cat}>{cat}</option>
                                ))}
                            </select>
                        </div>

                        {/* Subtitle */}
                        <div className="space-y-2 md:col-span-2">
                            <label className="text-sm text-text-muted">Subtítulo (Corto)</label>
                            <input
                                type="text"
                                name="subtitle"
                                value={formData.subtitle}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50"
                                placeholder="Ej: Relajante y aromático"
                            />
                        </div>

                        {/* Price */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Precio (€) *</label>
                            <input
                                required
                                type="number"
                                step="0.01"
                                name="price"
                                value={formData.price}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50"
                                placeholder="0.00"
                            />
                        </div>

                        {/* Stock */}
                        <div className="space-y-2">
                            <label className="text-sm text-text-muted">Stock Disponible *</label>
                            <input
                                required
                                type="number"
                                name="stock"
                                value={formData.stock}
                                onChange={handleChange}
                                className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50"
                                placeholder="0"
                            />
                        </div>
                    </div>
                </div>

                {/* Details & Image */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                    {/* Left Column: Details */}
                    <div className="lg:col-span-2 space-y-6">
                        <div className="bg-white/5 border border-white/10 rounded-2xl p-6 backdrop-blur-md space-y-6">
                            <h2 className="text-xl font-medium text-white border-b border-white/10 pb-4">Detalles</h2>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                {/* Brand */}
                                <div className="space-y-2">
                                    <label className="text-sm text-text-muted">Marca</label>
                                    <input
                                        type="text"
                                        name="brand"
                                        value={formData.brand}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white"
                                    />
                                </div>
                                {/* Code/SKU */}
                                <div className="space-y-2">
                                    <label className="text-sm text-text-muted">Código (SKU)</label>
                                    <input
                                        type="text"
                                        name="code"
                                        value={formData.code}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white"
                                    />
                                </div>
                                {/* Size */}
                                <div className="space-y-2">
                                    <label className="text-sm text-text-muted">Tamaño</label>
                                    <input
                                        type="text"
                                        name="size"
                                        value={formData.size}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white"
                                        placeholder="Ej: 100ml / M"
                                    />
                                </div>
                            </div>

                            {/* Description */}
                            <div className="space-y-2">
                                <label className="text-sm text-text-muted">Descripción Principal *</label>
                                <textarea
                                    required
                                    rows={4}
                                    name="description"
                                    value={formData.description}
                                    onChange={handleChange}
                                    className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50"
                                />
                            </div>

                            {/* Additional Description */}
                            <div className="space-y-2">
                                <label className="text-sm text-text-muted">Descripción Adicional (Extendido)</label>
                                <textarea
                                    rows={6}
                                    name="description_additional"
                                    value={formData.description_additional}
                                    onChange={handleChange}
                                    className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white placeholder-text-muted/50"
                                />
                            </div>
                        </div>

                        {/* Extra Info */}
                        <div className="bg-white/5 border border-white/10 rounded-2xl p-6 backdrop-blur-md space-y-6">
                            <h2 className="text-xl font-medium text-white border-b border-white/10 pb-4">Información Extra</h2>
                            <div className="grid grid-cols-1 gap-4">
                                <input name="usage" value={formData.usage} onChange={handleChange} placeholder="Modo de Uso" className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white" />
                                <input name="ingredients" value={formData.ingredients} onChange={handleChange} placeholder="Ingredientes" className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white" />
                                <input name="tips" value={formData.tips} onChange={handleChange} placeholder="Tips" className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white" />
                                <input name="sensation" value={formData.sensation} onChange={handleChange} placeholder="Sensación" className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl focus:outline-none focus:border-accent text-white" />
                            </div>
                        </div>
                    </div>

                    {/* Right Column: Image */}
                    <div className="space-y-6">
                        <div className="bg-white/5 border border-white/10 rounded-2xl p-6 backdrop-blur-md">
                            <h2 className="text-xl font-medium text-white mb-4">Imagen del Producto</h2>

                            <div className="flex flex-col items-center justify-center w-full">
                                <label className="flex flex-col items-center justify-center w-full h-64 border-2 border-dashed border-white/20 rounded-2xl cursor-pointer hover:bg-white/5 transition-colors relative overflow-hidden group">
                                    {imagePreview ? (
                                        <>
                                            <img src={imagePreview} alt="Preview" className="w-full h-full object-contain p-2" />
                                            <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 flex items-center justify-center transition-opacity">
                                                <Upload className="text-white" />
                                                <span className="ml-2 text-white font-medium">Cambiar</span>
                                            </div>
                                        </>
                                    ) : (
                                        <div className="flex flex-col items-center justify-center pt-5 pb-6 text-text-muted">
                                            <ImageIcon className="w-10 h-10 mb-3" />
                                            <p className="mb-2 text-sm"><span className="font-semibold">Click para subir</span></p>
                                            <p className="text-xs">PNG, JPG (MAX. 5MB)</p>
                                        </div>
                                    )}
                                    <input type="file" className="hidden" accept="image/*" onChange={handleImageChange} />
                                </label>
                            </div>
                        </div>

                        {/* Action Buttons */}
                        <div className="flex gap-4 pt-4">
                            <button
                                type="button"
                                onClick={() => navigate('/admin/products')}
                                className="flex-1 py-3 px-4 rounded-xl border border-white/10 text-white font-medium hover:bg-white/5 transition-colors"
                            >
                                Cancelar
                            </button>
                            <button
                                type="submit"
                                disabled={loading || uploading}
                                className="flex-1 py-3 px-4 rounded-xl bg-accent text-background-dark font-bold hover:bg-accent-light transition-colors shadow-lg shadow-accent/20 flex items-center justify-center gap-2"
                            >
                                {loading || uploading ? (
                                    <Loader2 className="animate-spin" size={20} />
                                ) : (
                                    <>
                                        <Save size={20} />
                                        Guardar
                                    </>
                                )}
                            </button>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default AdminProductForm;

import React, { useState } from 'react';
import { useAuth } from '../../features/auth/context/AuthContext';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            await login(email, password);
            navigate('/admin'); // Redirect to dashboard
        } catch (err) {
            setError('Credenciales incorrectas');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black flex items-center justify-center p-4">
            {/* Background Effects */}
            <div className="fixed inset-0 overflow-hidden pointer-events-none">
                <div className="absolute top-[-10%] left-[-10%] w-[40%] h-[40%] bg-purple-900/20 rounded-full blur-[120px]" />
                <div className="absolute bottom-[-10%] right-[-10%] w-[40%] h-[40%] bg-[#3FFFC1]/10 rounded-full blur-[120px]" />
            </div>

            <div className="w-full max-w-md relative z-10">
                <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-8 shadow-2xl">
                    <div className="text-center mb-8">
                        <h1 className="font-signature text-5xl text-accent mb-2">Perla Negra</h1>
                        <p className="text-text-muted text-xs tracking-wider uppercase">Acceso Administrativo</p>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-6">
                        {error && (
                            <div className="bg-red-500/10 border border-red-500/20 text-red-400 px-4 py-3 rounded-lg text-sm text-center">
                                {error}
                            </div>
                        )}

                        <div>
                            <label className="block text-text-muted text-xs uppercase tracking-wider mb-2">Email</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 text-white focus:border-[#3FFFC1] outline-none transition-colors"
                                placeholder="admin@perlanegra.com"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-text-muted text-xs uppercase tracking-wider mb-2">Contraseña</label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 text-white focus:border-[#3FFFC1] outline-none transition-colors"
                                placeholder="••••••••"
                                required
                            />
                        </div>

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full bg-[#3FFFC1] text-black font-medium py-3 rounded-lg hover:bg-[#32cc9a] transition-colors disabled:opacity-50 disabled:cursor-not-allowed uppercase tracking-wider text-sm"
                        >
                            {loading ? 'Accediendo...' : 'Ingresar al Portal'}
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;

import { chromium } from '@playwright/test';

async function globalSetup(config) {
    const { baseURL, storageState } = config.projects[0].use;
    const browser = await chromium.launch();
    const page = await browser.newPage();

    console.log(`Global Setup: Visiting ${baseURL}`);
    await page.goto(baseURL);

    // 1. Handle Age Gate
    try {
        const ageButton = page.getByRole('button', { name: /ho \+18 anni/i });
        if (await ageButton.isVisible({ timeout: 5000 })) {
            await ageButton.click();
            console.log('Global Setup: Age Gate dismissed');
        }
    } catch (e) { console.log('Global Setup: Age Gate not found'); }

    // 2. Handle Cookie Consent
    try {
        const cookieButton = page.getByRole('button', { name: /accetta/i });
        if (await cookieButton.isVisible({ timeout: 3000 })) {
            await cookieButton.click();
            console.log('Global Setup: Cookies accepted');
        }
    } catch (e) { console.log('Global Setup: Cookie Banner not found'); }

    // 3. Save State (Storage + Cookies)
    await page.context().storageState({ path: 'src/tests/e2e/storage-state.json' });
    console.log('Global Setup: State saved to src/tests/e2e/storage-state.json');

    await browser.close();
}

export default globalSetup;
